delete_firebase_user.js.content:
// Script to delete a Firebase user by email for testing purposes
import { initializeApp } from 'firebase-admin/app';
import { getAuth } from 'firebase-admin/auth';
import { credential } from 'firebase-admin';
import fs from 'fs';

const targetEmail = 'gopichandudhulipalla@gmail.com';

async function deleteFirebaseUserByEmail() {
  try {
    console.log('üî• Firebase User Deletion Script');
    console.log('===============================');
    console.log(`Target email: ${targetEmail}`);

    // Initialize Firebase Admin (using existing service account)
    const serviceAccountPath = './firebase-service-account.json';

    if (!fs.existsSync(serviceAccountPath)) {
      console.log('‚ùå Firebase service account file not found');
      console.log('   Creating from environment variables...');

      // Create service account file from environment variables
      const serviceAccount = {
        type: "service_account",
        project_id: process.env.FIREBASE_PROJECT_ID || "indieshots-c6bb1",
        private_key_id: process.env.FIREBASE_PRIVATE_KEY_ID,
        private_key: process.env.FIREBASE_PRIVATE_KEY?.replace(/\\n/g, '\n'),
        client_email: process.env.FIREBASE_CLIENT_EMAIL,
        client_id: process.env.FIREBASE_CLIENT_ID,
        auth_uri: "https://accounts.google.com/o/oauth2/auth",
        token_uri: "https://oauth2.googleapis.com/token"
      };

      if (!serviceAccount.private_key || !serviceAccount.client_email) {
        console.log('‚ùå Firebase environment variables not configured');
        console.log('   Cannot delete user without Firebase Admin SDK access');
        return;
      }

      fs.writeFileSync(serviceAccountPath, JSON.stringify(serviceAccount, null, 2));
      console.log('‚úÖ Service account file created');
    }

    // Initialize Firebase Admin
    const serviceAccount = JSON.parse(fs.readFileSync(serviceAccountPath, 'utf8'));

    const app = initializeApp({
      credential: credential.cert(serviceAccount),
      projectId: serviceAccount.project_id
    });

    const auth = getAuth(app);
    console.log('‚úÖ Firebase Admin initialized');

    // Find user by email
    console.log('üîç Searching for user...');
    let userRecord;

    try {
      userRecord = await auth.getUserByEmail(targetEmail);
      console.log(`‚úÖ User found: ${userRecord.uid}`);
      console.log(`   Email: ${userRecord.email}`);
      console.log(`   Created: ${userRecord.metadata.creationTime}`);
      console.log(`   Provider: ${userRecord.providerData[0]?.providerId || 'email'}`);
    } catch (error) {
      if (error.code === 'auth/user-not-found') {
        console.log('‚úÖ User not found in Firebase - email is available for registration');
        return;
      } else {
        console.log('‚ùå Error finding user:', error.message);
        return;
      }
    }

    // Delete the user
    console.log('üóëÔ∏è  Deleting user from Firebase...');
    try {
      await auth.deleteUser(userRecord.uid);
      console.log('‚úÖ User deleted successfully from Firebase');
      console.log(`   ${targetEmail} is now available for fresh registration`);
    } catch (error) {
      console.log('‚ùå Error deleting user:', error.message);
    }

  } catch (error) {
    console.error('‚ùå Script failed:', error.message);
  }
}

// Run the deletion
deleteFirebaseUserByEmail();

