import { Switch, Route } from "wouter";
import { queryClient } from "@/lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { UltimateAuthProvider } from "@/components/auth/UltimateAuthProvider";
import { ErrorBoundary } from "@/components/ErrorBoundary";
import { AuthConsistencyFix } from "@/components/AuthConsistencyFix";
import { ProTierProtection } from "@/components/ProTierProtection";
import { PaymentSuccessPopup } from "@/components/PaymentSuccessPopup";
import AppLayout from "@/components/layout/app-layout";
import AuthLayout from "@/components/layout/auth-layout";
import { ProtectedRoute } from "@/components/auth/protected-route";
import NotFound from "@/pages/not-found";

// Import existing pages
import Home from "@/pages/home";
import Features from "@/pages/features";
import SimpleAuth from "@/pages/simple-auth";
import CleanLogout from "@/pages/clean-logout";
import Dashboard from "@/pages/dashboard";
import Upload from "@/pages/upload";
import Parse from "@/pages/parse";
import SceneSelection from "@/pages/scene-selection";
import Columns from "@/pages/columns";
import Shots from "@/pages/shots";
import Storyboards from "@/pages/storyboards";
import Review from "@/pages/review";
import Projects from "@/pages/projects";
import Settings from "@/pages/settings";
import Upgrade from "@/pages/upgrade";
import TestPayment from "@/pages/test-payment";
import PayUCheckout from "@/pages/payu-checkout";
import Contact from "@/pages/contact";
import Admin from "@/pages/admin";
import Help from "@/pages/help";
import PublicFAQ from "@/pages/public-faq";
import VerifyEmail from "@/pages/verify-email";
import FilmAnimationsDemo from "@/pages/film-animations-demo";
import TestPromoFlow from "@/pages/test-promo-flow";
import ScriptHealthComingSoon from "@/pages/script-health-coming-soon";
import PaymentTest from "@/pages/payment-test";

function App() {
  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <UltimateAuthProvider>
          <PaymentSuccessPopup />
          <TooltipProvider>
            <Switch>
              {/* Authentication routes with clean layout */}
              <Route path="/auth">
                <AuthLayout>
                  <SimpleAuth />
                </AuthLayout>
              </Route>

              <Route path="/login">
                <AuthLayout>
                  <SimpleAuth />
                </AuthLayout>
              </Route>

              <Route path="/signup">
                <AuthLayout>
                  <SimpleAuth />
                </AuthLayout>
              </Route>

              <Route path="/verify-email">
                <AuthLayout>
                  <VerifyEmail />
                </AuthLayout>
              </Route>

              <Route path="/logout">
                <CleanLogout />
              </Route>

              {/* Home page with standalone layout */}
              <Route path="/">
                <Home />
              </Route>

              {/* Features page with standalone layout */}
              <Route path="/features">
                <Features />
              </Route>

              {/* Main application routes with app layout */}
              <Route path="/dashboard">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Dashboard />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/upload">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Upload />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/select-scenes/:projectId">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <SceneSelection id={params.projectId} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/scene-selection/:id">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <SceneSelection id={params.id} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/jobs/:id/scenes">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <SceneSelection id={params.id} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/parse/:id">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <Parse id={params.id} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/columns/:id">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <Columns id={params.id} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/shots/:projectId/:sceneIndex">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <Shots jobId={params.projectId} sceneIndex={params.sceneIndex} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/storyboards/:projectId/:sceneIndex">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <Storyboards jobId={params.projectId} sceneIndex={params.sceneIndex} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/review/:projectId">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <Review id={params.projectId} />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/projects">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Projects />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/settings">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Settings />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/upgrade">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Upgrade />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/payment-test">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <PaymentTest />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/test-payment">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <TestPayment />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/contact">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Contact />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/admin">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Admin />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/help">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <Help />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/film-animations-demo">
                <ProtectedRoute component={() => (
                  <AppLayout>
                    <FilmAnimationsDemo />
                  </AppLayout>
                )} />
              </Route>

              <Route path="/script-health/:scriptId">
                {(params) => (
                  <ProtectedRoute component={() => (
                    <AppLayout>
                      <ScriptHealthComingSoon />
                    </AppLayout>
                  )} />
                )}
              </Route>

              <Route path="/public-faq">
                <PublicFAQ />
              </Route>

              {/* 404 route */}
              <Route>
                <AppLayout>
                  <NotFound />
                </AppLayout>
              </Route>
            </Switch>
            <Toaster />
          </TooltipProvider>
        </UltimateAuthProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

export default App;

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Cinematic Shot Animation Styles */
@keyframes float-slow {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes float-delay-1 {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-15px); }
}

@keyframes float-delay-2 {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-25px); }
}

@keyframes float-delay-3 {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-18px); }
}

@keyframes twinkle {
  0%, 100% { opacity: 0.2; transform: scale(1); }
  50% { opacity: 0.8; transform: scale(1.2); }
}

@keyframes twinkle-delay-1 {
  0%, 100% { opacity: 0.3; transform: scale(1); }
  50% { opacity: 0.9; transform: scale(1.3); }
}

@keyframes twinkle-delay-2 {
  0%, 100% { opacity: 0.4; transform: scale(1); }
  50% { opacity: 0.7; transform: scale(1.1); }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes spin-reverse {
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
}

@keyframes pulse-glow {
  0%, 100% { box-shadow: 0 0 10px rgba(99, 102, 241, 0.3); }
  50% { box-shadow: 0 0 20px rgba(99, 102, 241, 0.5), 0 0 30px rgba(129, 140, 248, 0.3); }
}

@keyframes bounce-subtle {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-5px); }
}

@keyframes orbit {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes orbit-reverse {
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
}

@keyframes orbit-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes orbit-reverse-slow {
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
}

@keyframes gradient-shift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@keyframes pulse-sequence {
  0%, 60%, 100% { opacity: 0.3; }
  30% { opacity: 1; }
}

@keyframes pulse-sequence-delay-1 {
  0%, 60%, 100% { opacity: 0.3; }
  30% { opacity: 1; }
}

@keyframes pulse-sequence-delay-2 {
  0%, 60%, 100% { opacity: 0.3; }
  30% { opacity: 1; }
}

@keyframes fade-in-out {
  0%, 100% { opacity: 0.7; }
  50% { opacity: 1; }
}

@keyframes film-strip {
  0%, 100% { transform: scaleY(1); }
  50% { transform: scaleY(1.2); }
}

@keyframes grain {
  0%, 100% { transform: translate(0, 0); }
  10% { transform: translate(-5%, -10%); }
  20% { transform: translate(-15%, 5%); }
  30% { transform: translate(7%, -25%); }
  40% { transform: translate(-5%, 25%); }
  50% { transform: translate(-15%, 10%); }
  60% { transform: translate(15%, 0%); }
  70% { transform: translate(0%, 15%); }
  80% { transform: translate(3%, 35%); }
  90% { transform: translate(-10%, 10%); }
}

@keyframes slide-stripe {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

/* Animation Classes */
.animate-float-slow { animation: float-slow 4s ease-in-out infinite; }
.animate-float-delay-1 { animation: float-delay-1 3.5s ease-in-out infinite 0.5s; }
.animate-float-delay-2 { animation: float-delay-2 4.5s ease-in-out infinite 1s; }
.animate-float-delay-3 { animation: float-delay-3 3.8s ease-in-out infinite 1.5s; }
.animate-twinkle { animation: twinkle 2s ease-in-out infinite; }
.animate-twinkle-delay-1 { animation: twinkle-delay-1 2.5s ease-in-out infinite 0.5s; }
.animate-twinkle-delay-2 { animation: twinkle-delay-2 3s ease-in-out infinite 1s; }
.animate-spin-slow { animation: spin-slow 8s linear infinite; }
.animate-spin-reverse { animation: spin-reverse 6s linear infinite; }
.animate-pulse-glow { animation: pulse-glow 3s ease-in-out infinite; }
.animate-bounce-subtle { animation: bounce-subtle 2s ease-in-out infinite; }
.animate-orbit { animation: orbit 12s linear infinite; }
.animate-orbit-reverse { animation: orbit-reverse 10s linear infinite; }
.animate-orbit-slow { animation: orbit-slow 15s linear infinite; }
.animate-orbit-reverse-slow { animation: orbit-reverse-slow 18s linear infinite; }
.animate-gradient-shift { animation: gradient-shift 4s ease-in-out infinite; background-size: 200% 200%; }
.animate-pulse-sequence { animation: pulse-sequence 2s ease-in-out infinite; }
.animate-pulse-sequence-delay-1 { animation: pulse-sequence-delay-1 2s ease-in-out infinite 0.6s; }
.animate-pulse-sequence-delay-2 { animation: pulse-sequence-delay-2 2s ease-in-out infinite 1.2s; }
.animate-fade-in-out { animation: fade-in-out 3s ease-in-out infinite; }
.animate-film-strip { animation: film-strip 1.5s ease-in-out infinite; }
.animate-grain { animation: grain 8s steps(10) infinite; }
.animate-slide-stripe { animation: slide-stripe 2s ease-in-out infinite; }

/* Background Grain Pattern */
.bg-grain {
  background-image: 
    radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255, 255, 255, 0.15) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(120, 119, 198, 0.2) 0%, transparent 50%);
}

:root {
  --background: 0 0% 100%;
  --foreground: 20 14.3% 4.1%;
  --muted: 60 4.8% 95.9%;
  --muted-foreground: 25 5.3% 44.7%;
  --popover: 0 0% 100%;
  --popover-foreground: 20 14.3% 4.1%;
  --card: 0 0% 100%;
