  --muted: 240 4% 12%;
  --muted-foreground: 240 5% 64.9%;
  --popover: 225 14% 10%; /* #141824 */
  --popover-foreground: 0 0% 98%;
  --card: 229 13% 15%; /* #1E2130 */
  --card-foreground: 0 0% 98%;
  --border: 240 4% 16%;
  --input: 240 4% 16%;
  --primary: 260 100% 30%; /* #330099 */
  --primary-foreground: 211 100% 99%;
  --secondary: 229 13% 15%;
  --secondary-foreground: 0 0% 98%;
  --accent: 260 80% 40%; /* #5500CC */
  --accent-foreground: 0 0% 98%;
  --destructive: 0 62.8% 30.6%;
  --destructive-foreground: 0 0% 98%;
  --ring: 240 4.9% 83.9%;
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  * {
    transition: background-color 0.2s ease, color 0.2s ease, border-color 0.2s ease;
  }

  body {
    @apply antialiased bg-background text-foreground;
    font-family: var(--font-sans);
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply text-foreground;
    font-family: var(--font-heading);
    font-weight: 600;
    letter-spacing: -0.02em;
    line-height: 1.2;
  }

  h1 {
    font-size: 2.25rem;
    font-weight: 700;
  }

  h2 {
    font-size: 1.875rem;
  }

  h3 {
    font-size: 1.5rem;
  }

  h4 {
    font-size: 1.25rem;
  }

  p {
    @apply text-foreground;
    line-height: 1.6;
  }

  code, pre {
    font-family: var(--font-mono);
  }

  /* Ensure all text elements use proper theme colors */
  span, div, label, button, input, textarea {
    @apply text-foreground;
  }

  /* Override any conflicting styles */
  .dark *, .light * {
    color: inherit !important;
  }
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

/* Progress Steps Styling */
.progress-step::after {
  content: '';
  height: 2px;
  width: 100%;
  background-color: hsl(var(--muted));
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: -1;
}

.progress-step:last-child::after {
  display: none;
}

/* Custom Storyboard Loading Animations */
@keyframes wiggle {
  0%, 100% { transform: rotate(-8deg); }
  50% { transform: rotate(8deg); }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px) scale(1); }
  50% { transform: translateY(-12px) scale(1.1); }
}

@keyframes bounce-out {
  0%, 100% { transform: translateY(0px) scale(1); }
  50% { transform: translateY(-16px) scale(1.2); }
}

@keyframes pulse-scale {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.3); opacity: 0.8; }
}

@keyframes ping-pop {
  0% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.4); opacity: 0.6; }
  100% { transform: scale(1); opacity: 1; }
}

.animate-wiggle {
  animation: wiggle 1.2s ease-in-out infinite;
}

.animate-spin-slow {
  animation: spin-slow 2.5s linear infinite;
}

.animate-float {
  animation: float 2.5s ease-in-out infinite;
}

.animate-bounce {
  animation: bounce-out 1.5s ease-in-out infinite;
}

.animate-pulse {
  animation: pulse-scale 2s ease-in-out infinite;
}

.animate-ping {
  animation: ping-pop 1.8s ease-in-out infinite;
}

