/**
 * Reset password with token
 */
export async function resetPassword(req: Request, res: Response) {
  try {
    // Validate request data
    const { token, password } = passwordResetSchema.parse(req.body);

    // Find user by reset token
    const user = await storage.getUserByResetToken(token);

    if (!user || !user.resetTokenExpiry) {
      return res.status(400).json({ message: 'Invalid or expired reset token' });
    }

    // Check if token is expired
    const now = new Date();
    if (now > user.resetTokenExpiry) {
      return res.status(400).json({ message: 'Reset token has expired' });
    }

    // Hash the new password
    const salt = await bcrypt.genSalt(10);
    const hashedPassword = await bcrypt.hash(password, salt);

    // Update user with new password and clear reset token
    await storage.updateUser(user.id, {
      password: hashedPassword,
      resetToken: null,
      resetTokenExpiry: null,
      updatedAt: new Date()
    });

    res.json({ message: 'Password has been reset successfully' });
  } catch (error) {
    console.error('Reset password error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: 'Validation error', 
        errors: error.errors 
      });
    }

    res.status(500).json({ message: 'Server error during password reset' });
  }
}



/**
 * Log out the user
 */
export async function logout(req: Request, res: Response) {
  try {
    console.log('=== PROCESSING LOGOUT REQUEST ===');

    // Get the token from cookies to invalidate it
    const token = req.cookies?.auth_token;
    if (token) {
      invalidateToken(token);
      console.log('‚úì Token invalidated and blacklisted');
    }

    // Comprehensive cookie clearing strategy
    const cookieOptions = {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'lax' as const,
      path: '/'
    };

    // Method 1: Clear with exact same options used when setting
    res.clearCookie('auth_token', cookieOptions);

    // Method 2: Clear with basic options
    res.clearCookie('auth_token', { path: '/' });
    res.clearCookie('auth_token');

    // Method 3: Set expired cookie to force removal
    res.cookie('auth_token', '', {
      ...cookieOptions,
      maxAge: 0,
      expires: new Date(0)
    });

    // Method 4: Set different path variations to ensure removal
    res.clearCookie('auth_token', { path: '/', domain: undefined });

    // Clear all authentication-related headers
    res.removeHeader('Set-Cookie');

    // Clear session data if it exists
    if (req.session) {
      req.session.destroy((err) => {
        if (err) {
          console.error('Session destroy error:', err);
        }
      });
    }

    // Add headers to prevent caching
    res.set({
      'Cache-Control': 'no-cache, no-store, must-revalidate',
      'Pragma': 'no-cache',
      'Expires': '0'
    });

    console.log('‚úì LOGOUT COMPLETED - All cookies cleared and token invalidated');
    res.json({ 
      message: 'Logged out successfully',
      timestamp: Date.now(),
      cookiesCleared: true 
    });
  } catch (error) {
    console.error('Logout error:', error);
    res.status(500).json({ message: 'Error during logout' });
  }
}

/**
 * Get current user's profile
 */
export async function getProfile(req: Request, res: Response) {
  try {
    if (!req.user) {
      return res.status(401).json({ message: 'User not authenticated' });
    }
    const userId = req.user.id;
    const user = await storage.getUser(userId);

    if (!user) {
      return res.status(404).json({ message: 'User not found' });
    }

    // Return user data (without sensitive fields)
    res.json({
      id: user.id,
      email: user.email,
      firstName: user.firstName,
      lastName: user.lastName,
      profileImageUrl: user.profileImageUrl,
      provider: user.provider,
      tier: user.tier,
      totalPages: user.totalPages,
      usedPages: user.usedPages,
      emailVerified: user.emailVerified,
      createdAt: user.createdAt
    });
  } catch (error) {
    console.error('Get profile error:', error);
    res.status(500).json({ message: 'Server error fetching profile' });
  }
}

/**
 * Update user profile
 */
export async function updateProfile(req: Request, res: Response) {
  try {
    if (!req.user) {
      return res.status(401).json({ message: 'User not authenticated' });
    }
    const userId = req.user.id;
    const { firstName, lastName, profileImageUrl } = req.body;

    // Update only allowed fields
    const updatedUser = await storage.updateUser(userId, {
      firstName: firstName || undefined,
      lastName: lastName || undefined,
      profileImageUrl: profileImageUrl || undefined,
      updatedAt: new Date()
    });

    res.json({
      id: updatedUser.id,
      email: updatedUser.email,
      firstName: updatedUser.firstName,
      lastName: updatedUser.lastName,
      profileImageUrl: updatedUser.profileImageUrl,
      provider: updatedUser.provider,
      tier: updatedUser.tier,
      emailVerified: updatedUser.emailVerified
    });
  } catch (error) {
    console.error('Update profile error:', error);
    res.status(500).json({ message: 'Server error updating profile' });
  }
}

/**
 * Change user password
 */
export async function changePassword(req: Request, res: Response) {
  try {
    if (!req.user) {
      return res.status(401).json({ message: 'User not authenticated' });
    }
    // Validate request data
    const { currentPassword, newPassword } = passwordChangeSchema.parse(req.body);

    const userId = req.user.id;
    const user = await storage.getUser(userId);

    if (!user) {
      return res.status(404).json({ message: 'User not found' });
    }

    // Check if user has a password (could be OAuth user)
    if (!user.password || user.provider !== 'local') {
      return res.status(400).json({ message: 'Password change not available for this account type' });
    }

    // Verify current password
    const isPasswordValid = await bcrypt.compare(currentPassword, user.password);

    if (!isPasswordValid) {
      return res.status(401).json({ message: 'Current password is incorrect' });
    }

    // Hash the new password
    const salt = await bcrypt.genSalt(10);
    const hashedPassword = await bcrypt.hash(newPassword, salt);

    // Update user with new password
    await storage.updateUser(userId, {
      password: hashedPassword,
      updatedAt: new Date()
    });

    res.json({ message: 'Password changed successfully' });
  } catch (error) {
    console.error('Change password error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: 'Validation error', 
        errors: error.errors 
      });
    }

    res.status(500).json({ message: 'Server error changing password' });
  }
}

