import crypto from 'crypto';

export interface PayUConfig {
  merchantKey: string;
  merchantSalt: string;
  clientId: string;
  clientSecret: string;
  baseUrl: string;
}

export interface PaymentParams {
  key: string;
  txnid: string;
  amount: string;
  productinfo: string;
  firstname: string;
  email: string;
  phone: string;
  surl: string;
  furl: string;
  hash: string;
  service_provider: string;
  lastname?: string;
  address1?: string;
  address2?: string;
  city?: string;
  state?: string;
  country?: string;
  zipcode?: string;
  udf1?: string;
  udf2?: string;
  udf3?: string;
  udf4?: string;
  udf5?: string;
}

export interface PaymentResponse {
  txnid: string;
  amount: string;
  productinfo: string;
  firstname: string;
  email: string;
  mihpayid: string;
  status: string;
  hash: string;
  phone?: string;
}

/**
 * Professional PayU Payment Gateway Service
 * Secure real-time payment processing with production credentials
 * All credentials are in LIVE MODE for real transactions
 */
export class PayUService {
  private config: PayUConfig;

  constructor() {
    // Production credentials - LIVE MODE ONLY
    this.config = {
      merchantKey: 'xXZDKp', // Live merchant key
      merchantSalt: 'ezsXEEqchsA1ZLmHzn5BrLRl9snmckHn', // Live salt (33 chars)
      clientId: 'f10a90386f9639dadfe839bc565d2e6d26cb5d88e1f49640b53960ed0d1364c8', // Live client ID
      clientSecret: 'd2d92cbf109d9efe6430ec8399c5ffc89287b5fcfe6e8f27713a0fc17f3b74ec', // Live client secret
      baseUrl: 'https://secure.payu.in' // Production gateway
    };

    console.log('üîí PayU PRODUCTION Service Initialized');
    console.log(`üì° Gateway: ${this.config.baseUrl}`);
    console.log(`üîë Merchant Key: ${this.config.merchantKey}`);
    console.log(`üßÇ Salt Length: ${this.config.merchantSalt.length} characters`);
    console.log(`üÜî Client ID: ${this.config.clientId.substring(0, 8)}...`);
    console.log('‚úÖ LIVE payment processing enabled - Real money transactions');
  }

  /**
   * Generate secure hash for payment request using PayU OFFICIAL format
   * OFFICIAL Format: key|txnid|amount|productinfo|firstname|email|udf1|udf2|udf3|udf4|udf5||||||SALT
   * Critical: 6 empty pipes after udf5, not 5!
   */
  generatePaymentHash(params: Omit<PaymentParams, 'hash'>): string {
    const udf1 = params.udf1 || '';
    const udf2 = params.udf2 || '';
    const udf3 = params.udf3 || '';
    const udf4 = params.udf4 || '';
    const udf5 = params.udf5 || '';

    // PayU OFFICIAL hash format - CORRECTED to match official documentation
    // Format: key|txnid|amount|productinfo|firstname|email|udf1|udf2|udf3|udf4|udf5||||||SALT
    // Empty UDF fields: email|||||||SALT (7 pipes after email, 14 total)
    const hashString = `${params.key}|${params.txnid}|${params.amount}|${params.productinfo}|${params.firstname}|${params.email}|||||||||${this.config.merchantSalt}`;

    console.log('üîê PayU OFFICIAL Hash Generation (CORRECTED)');
    console.log(`Key: ${params.key}`);
    console.log(`TxnID: ${params.txnid}`);
    console.log(`Amount: ${params.amount}`);
    console.log(`ProductInfo: ${params.productinfo}`);
    console.log(`FirstName: ${params.firstname}`);
    console.log(`Email: ${params.email}`);
    console.log(`UDF Fields: Empty (11 pipes)`);
    console.log(`Salt: ${this.config.merchantSalt}`);
    console.log(`OFFICIAL Hash String: ${hashString}`);

    // Count pipes for verification
    const pipeCount = (hashString.match(/\|/g) || []).length;
    console.log(`Total Pipes: ${pipeCount} (PayU requires specific count)`);

    const hash = crypto.createHash('sha512').update(hashString).digest('hex');
    console.log(`Generated SHA512 Hash: ${hash.substring(0, 64)}...`);

    return hash;
  }

  /**
   * Generate hash for payment response verification
   */
  generateResponseHash(response: PaymentResponse): string {
    const udf1 = (response as any).udf1 || '';
    const udf2 = (response as any).udf2 || '';
    const udf3 = (response as any).udf3 || '';
    const udf4 = (response as any).udf4 || '';
    const udf5 = (response as any).udf5 || '';

    // Response hash format (reverse order)
    const hashString = `${this.config.merchantSalt}|${response.status}|${udf5}|${udf4}|${udf3}|${udf2}|${udf1}|${response.email}|${response.firstname}|${response.productinfo}|${response.amount}|${response.txnid}|${this.config.merchantKey}`;

    console.log('üîê PayU Response Hash Verification');
    console.log(`Response Hash String: ${hashString}`);

    return crypto.createHash('sha512').update(hashString).digest('hex').toLowerCase();
  }

  /**
   * Create secure payment parameters for PayU gateway
   * Amount set to 1 rupee for testing as requested
   */
  createPaymentParams(
    amount: number,
    email: string,
    firstname: string,
    phone: string = '9999999999',
    tier: string = 'pro'
  ): PaymentParams {
    // Generate unique transaction ID
    const txnid = `INDIE_${Date.now()}_${Math.random().toString(36).substring(2, 11)}`;

    // Get current domain for callbacks
    const domain = process.env.REPLIT_DEV_DOMAIN 
      ? `https://${process.env.REPLIT_DEV_DOMAIN}`
      : 'https://workspace.shruti37.replit.app';

    const paymentParams: Omit<PaymentParams, 'hash'> = {
      key: this.config.merchantKey,
      txnid: txnid,
      amount: amount.toFixed(2), // Use actual amount (1.00 for testing)
      productinfo: 'IndieShots_Pro_Upgrade',
      firstname: firstname,
      email: email,
      phone: phone,
      surl: `${domain}/api/payu/success`, // Success URL
      furl: `${domain}/api/payu/failure`, // Failure URL
      service_provider: 'payu_paisa',
      lastname: '',
      address1: '',
      address2: '',
      city: '',
      state: '',
      country: 'India',
      zipcode: '',
      udf1: tier, // Store tier in UDF1
      udf2: '',
      udf3: '',
      udf4: '',
      udf5: ''
    };

    // Generate secure hash
    const hash = this.generatePaymentHash(paymentParams);

    console.log('üí∞ Payment Parameters Created (PRODUCTION)');
    console.log(`Transaction ID: ${txnid}`);
    console.log(`Amount: ‚Çπ${amount} (Real money)`);
    console.log(`Email: ${email}`);
    console.log(`Merchant: ${this.config.merchantKey}`);
    console.log('üö® This is a LIVE transaction with real money');

    return {
      ...paymentParams,
      hash: hash
    };
  }

  /**
   * Verify payment response hash for security
   */
  verifyPaymentResponse(response: PaymentResponse): boolean {
    const expectedHash = this.generateResponseHash(response);
    const receivedHash = response.hash.toLowerCase();

    const isValid = expectedHash === receivedHash;

    console.log('üîí Payment Response Verification');
    console.log(`Expected Hash: ${expectedHash.substring(0, 32)}...`);
    console.log(`Received Hash: ${receivedHash.substring(0, 32)}...`);
    console.log(`Verification: ${isValid ? '‚úÖ VALID' : '‚ùå INVALID'}`);

    return isValid;
  }

  /**
   * Get PayU gateway URL
   */
  getGatewayUrl(): string {
    return `${this.config.baseUrl}/_payment`;
  }

  /**
   * Get merchant configuration (safe for logging)
   */
  getConfig() {
    return {
      merchantKey: this.config.merchantKey,
      baseUrl: this.config.baseUrl,
      saltLength: this.config.merchantSalt.length,
      clientIdPreview: this.config.clientId.substring(0, 8) + '...'
    };
  }
}

server/services/promoCodeService.ts.content:
import { db } from '../db';
import { promoCodes, promoCodeUsage, users } from '../../shared/schema';
import { eq, and, gte, desc, count } from 'drizzle-orm';
import type { PromoCode, PromoCodeUsage, InsertPromoCodeUsage } from '../../shared/schema';

export interface PromoCodeValidation {
  isValid: boolean;
  tier?: string;
  errorMessage?: string;
  usageCount?: number;
  remainingUses?: number;
  promoCodeId?: number;
}

export interface PromoCodeStats {
  totalUses: number;
  uniqueUsers: number;
  usageByDate: { date: string; count: number }[];
  recentUsage: Array<{
    email: string;
    usedAt: Date;
    ipAddress: string;
    userAgent?: string;
  }>;
  remainingUses: number;
  usageLimit: number;
}

export interface RateLimitInfo {
  maxAttemptsPerIP: number;
  maxAttemptsPerEmail: number;
  cooldownPeriod: number; // milliseconds
}

// Rate limiting configuration
const RATE_LIMITS: RateLimitInfo = {
  maxAttemptsPerIP: 10, // per hour
  maxAttemptsPerEmail: 5, // per hour
  cooldownPeriod: 3600000 // 1 hour in milliseconds
};

// In-memory rate limiting (in production, use Redis)
const rateLimitStore = new Map<string, { attempts: number; lastAttempt: number }>();

export class PromoCodeService {

  /**
   * Check rate limiting for IP and email
   */
  private checkRateLimit(key: string, maxAttempts: number): boolean {
    const now = Date.now();
    const record = rateLimitStore.get(key);

    if (!record) {
      rateLimitStore.set(key, { attempts: 1, lastAttempt: now });
      return true;
    }

    // Reset if cooldown period has passed
    if (now - record.lastAttempt > RATE_LIMITS.cooldownPeriod) {
      rateLimitStore.set(key, { attempts: 1, lastAttempt: now });
      return true;
    }

    // Check if under limit
    if (record.attempts < maxAttempts) {
      record.attempts++;
      record.lastAttempt = now;
      return true;
    }

    return false;
  }

  /**
   * Validate if a promo code can be used today by a specific user
   */
  async validatePromoCode(
    code: string, 
    userEmail: string, 
    ipAddress?: string
  ): Promise<PromoCodeValidation> {
    try {
      // Rate limiting checks
      if (ipAddress && !this.checkRateLimit(`ip:${ipAddress}`, RATE_LIMITS.maxAttemptsPerIP)) {
        return {
          isValid: false,
          errorMessage: 'Too many attempts from this IP. Please try again later.'
        };
      }

      if (!this.checkRateLimit(`email:${userEmail}`, RATE_LIMITS.maxAttemptsPerEmail)) {
        return {
          isValid: false,
          errorMessage: 'Too many attempts for this email. Please try again later.'
        };
      }

      // Get promo code from database
      const promoCode = await db.select()
        .from(promoCodes)
        .where(and(
          eq(promoCodes.code, code.toUpperCase()),
          eq(promoCodes.isActive, true)
        ))
        .limit(1);

      if (promoCode.length === 0) {
        return {
          isValid: false,
          errorMessage: 'Invalid promo code'
        };
      }

      const promo = promoCode[0];

      // Check if today is a valid date
      const today = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
      if (!promo.validDates.includes(today)) {
        return {
          isValid: false,
          errorMessage: `Promo code is only valid on specific dates: ${promo.validDates.join(', ')}`
        };
      }

      // Check usage limit
      const usageLimit = promo.usageLimit ?? -1;
      const currentUsage = promo.currentUsage ?? 0;

      if (usageLimit !== -1 && currentUsage >= usageLimit) {
        return {
          isValid: false,
          errorMessage: 'Promo code usage limit reached'
        };
      }

      // Check if user already used this code
      const existingUsage = await db.select()
        .from(promoCodeUsage)
        .where(and(
          eq(promoCodeUsage.promoCodeId, promo.id),
          eq(promoCodeUsage.userEmail, userEmail.toLowerCase())
        ))
        .limit(1);

      if (existingUsage.length > 0) {
        return {
          isValid: false,
          errorMessage: 'You have already used this promo code'
        };
      }

      // Calculate remaining uses
      const remainingUses = usageLimit === -1 ? -1 : usageLimit - currentUsage;

      return {
        isValid: true,
        tier: promo.tierGranted || 'pro',
        usageCount: currentUsage,
        remainingUses,
        promoCodeId: promo.id
      };

    } catch (error) {
      console.error('Error validating promo code:', error);
      return {
        isValid: false,
        errorMessage: 'Error validating promo code'
      };
    }
  }

  /**
   * Apply a promo code to a user account
   */
  async applyPromoCode(
    code: string, 
    userEmail: string, 
    userId: string,
    ipAddress?: string,
    userAgent?: string
  ): Promise<boolean> {
    try {
      // Validate the promo code first
      console.log(`üéØ PROMO CODE APPLICATION: ${code.toUpperCase()} for user ${userEmail}`);

      const validation = await this.validatePromoCode(code, userEmail, ipAddress);

      if (!validation.isValid || !validation.promoCodeId) {
        console.error(`‚ùå Cannot apply invalid promo code ${code.toUpperCase()}:`, validation.errorMessage);
        return false;
      }

      console.log(`‚úÖ PROMO CODE VALIDATED: ${code.toUpperCase()} grants ${validation.tier} tier to ${userEmail}`);

      // Create usage record
      const usageData: InsertPromoCodeUsage = {
        promoCodeId: validation.promoCodeId,
        userEmail: userEmail.toLowerCase(),
        userId,
        grantedTier: validation.tier,
        ipAddress,
        userAgent
      };

      await db.insert(promoCodeUsage).values(usageData);

      // FIREBASE-FIRST APPROACH: Only update users who exist in Firebase
      // This ensures data consistency - no PostgreSQL users without Firebase users

      // First, verify if user exists in Firebase
      let userExistsInFirebase = false;
      try {
        const admin = await import('firebase-admin');
        const firebaseAdmin = admin.default;

        if (firebaseAdmin.apps.length) {
          await firebaseAdmin.auth().getUserByEmail(userEmail.toLowerCase());
          userExistsInFirebase = true;
          console.log(`‚úì Verified user ${userEmail} exists in Firebase`);
        }
      } catch (error) {
        console.log(`User ${userEmail} not found in Firebase - will be handled during signup`);
      }

      if (userExistsInFirebase) {
        // User exists in Firebase, check if they also exist in PostgreSQL
        const existingUser = await db.select()
          .from(users)
          .where(eq(users.email, userEmail.toLowerCase()))
          .limit(1);

        if (existingUser.length > 0) {
          // Update existing PostgreSQL user's tier
          await db.update(users)
            .set({ 
              tier: validation.tier,
              totalPages: validation.tier === 'pro' ? -1 : 10,
              maxShotsPerScene: validation.tier === 'pro' ? -1 : 5,
              canGenerateStoryboards: validation.tier === 'pro',
              updatedAt: new Date()
            })
            .where(eq(users.email, userEmail.toLowerCase()));

          // Also update user_quotas table for comprehensive tier synchronization
          if (validation.tier === 'free' || validation.tier === 'pro') {
            try {
              const { productionQuotaManager } = await import('../lib/productionQuotaManager');
              await productionQuotaManager.updateUserTier(userId, validation.tier);
              console.log(`‚úì Updated user_quotas table for ${userEmail} to tier: ${validation.tier}`);
            } catch (error) {
              console.log(`‚ö†Ô∏è Could not update user_quotas table:`, error);
            }
          }

          console.log(`‚úì Updated existing Firebase+PostgreSQL user ${userEmail} to tier: ${validation.tier}`);
        } else {
          console.log(`‚úì Firebase user exists but not in PostgreSQL - will sync on next signin`);
        }
      } else {
        console.log(`‚úì New user - tier will be set via Firebase custom claims during signup`);
      }

      // Update promo code usage count
      await db.update(promoCodes)
        .set({ 
          currentUsage: (validation.usageCount ?? 0) + 1,
          updatedAt: new Date()
        })
        .where(eq(promoCodes.id, validation.promoCodeId));

      console.log(`‚úì Promo code ${code} applied for user ${userEmail}, tier upgraded to ${validation.tier}`);
      return true;

    } catch (error) {
      console.error('Error applying promo code:', error);
      return false;
    }
  }

  /**
   * Check if user has already used a specific promo code
   */
  async hasUserUsedCode(code: string, userEmail: string): Promise<boolean> {
    try {
      const promoCode = await db.select({ id: promoCodes.id })
        .from(promoCodes)
        .where(eq(promoCodes.code, code.toUpperCase()))
        .limit(1);

      if (promoCode.length === 0) {
        return false;
      }

      const usage = await db.select()
        .from(promoCodeUsage)
        .where(and(
          eq(promoCodeUsage.promoCodeId, promoCode[0].id),
          eq(promoCodeUsage.userEmail, userEmail.toLowerCase())
        ))
        .limit(1);

      return usage.length > 0;

    } catch (error) {
      console.error('Error checking promo code usage:', error);
      return false;
    }
  }

  /**
   * Get comprehensive statistics for a promo code
   */
  async getPromoCodeStats(code: string): Promise<PromoCodeStats | null> {
    try {
      // Get promo code info
      const promoCode = await db.select()
        .from(promoCodes)
        .where(eq(promoCodes.code, code.toUpperCase()))
        .limit(1);

      if (promoCode.length === 0) {
        return null;
      }

      const promo = promoCode[0];

      // Get total usage count
      const totalUsage = await db.select({ count: count() })
        .from(promoCodeUsage)
        .where(eq(promoCodeUsage.promoCodeId, promo.id));

      // Get unique users count
      const uniqueUsers = await db.selectDistinct({ userEmail: promoCodeUsage.userEmail })
        .from(promoCodeUsage)
        .where(eq(promoCodeUsage.promoCodeId, promo.id));

      // Get recent usage (last 50)
      const recentUsage = await db.select({
        email: promoCodeUsage.userEmail,
        usedAt: promoCodeUsage.usedAt,
        ipAddress: promoCodeUsage.ipAddress,
        userAgent: promoCodeUsage.userAgent
      })
        .from(promoCodeUsage)
        .where(eq(promoCodeUsage.promoCodeId, promo.id))
        .orderBy(desc(promoCodeUsage.usedAt))
        .limit(50);

      // Calculate remaining uses
      const usageLimit = promo.usageLimit ?? -1;
      const currentUsage = promo.currentUsage ?? 0;
      const remainingUses = usageLimit === -1 ? -1 : usageLimit - currentUsage;

      return {
        totalUses: totalUsage[0]?.count || 0,
        uniqueUsers: uniqueUsers.length,
        usageByDate: [], // Could be implemented with more complex query
        recentUsage: recentUsage.map(usage => ({
          email: usage.email,
          usedAt: usage.usedAt,
          ipAddress: usage.ipAddress || 'Unknown',
          userAgent: usage.userAgent || undefined
        })),
        remainingUses,
        usageLimit: usageLimit
      };

    } catch (error) {
      console.error('Error getting promo code stats:', error);
      return null;
    }
  }

  /**
   * Get all active promo codes (admin only)
   */
  async getAllPromoCodes(): Promise<PromoCode[]> {
    try {
      return await db.select()
        .from(promoCodes)
        .where(eq(promoCodes.isActive, true))
        .orderBy(desc(promoCodes.createdAt));

    } catch (error) {
      console.error('Error getting all promo codes:', error);
      return [];
    }
  }

  /**
   * Check if current date is valid for any promo codes
   */
  isValidDateForPromoCodes(): boolean {
    const today = new Date().toISOString().split('T')[0];
    const validDates = ['2025-07-03', '2025-07-06', '2025-07-07', '2025-07-26', '2025-07-27'];
    return validDates.includes(today);
  }

  /**
   * Delete all promo code usage records for a user (for permanent account deletion)
   */
  static async deleteUserPromoCodeUsage(email: string): Promise<void> {
    try {
      await db.delete(promoCodeUsage)
        .where(eq(promoCodeUsage.userEmail, email.toLowerCase()));

      console.log(`Deleted promo code usage records for user: ${email}`);
    } catch (error) {
      console.error('Error deleting promo code usage records:', error);
      throw error;
    }
  }
}

// Export singleton instance
export const promoCodeService = new PromoCodeService();

