import { useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { ArrowRight, ArrowLeft, Play, Download, Loader2 } from "lucide-react";

interface ParseProps {
  id: string;
}

export default function Parse({ id }: ParseProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch parse job details
  const { data: parseJob, isLoading: isLoadingJob } = useQuery({
    queryKey: [`/api/jobs/${id}`],
    refetchInterval: 1000, // Poll every second to catch status changes
  });

  // Fetch associated script
  const { data: script, isLoading: isLoadingScript } = useQuery({
    queryKey: [`/api/scripts/${(parseJob as any)?.scriptId}`],
    enabled: !!(parseJob as any)?.scriptId,
  });

  // Parse script mutation
  const parseMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", `/api/jobs/${id}/parse`, {});
    },
    onSuccess: () => {
      toast({
        title: "Processing started",
        description: "Your script is being parsed."
      });
      queryClient.invalidateQueries({ queryKey: [`/api/jobs/${id}`] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to start parsing",
        description: error.message || "An error occurred",
        variant: "destructive"
      });
    }
  });

  // CSV Download function
  const downloadCSV = () => {
    const link = document.createElement('a');
    link.href = `/api/jobs/${id}/download`;
    link.download = `script-analysis-${id}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Auto-start parsing when page loads if job is pending
  useEffect(() => {
    if ((parseJob as any)?.status === 'pending' && !parseMutation.isPending) {
      // Automatically start parsing after a short delay
      const timer = setTimeout(() => {
        parseMutation.mutate();
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [(parseJob as any)?.status, parseMutation]);

  // Auto-navigate to review page when processing completes
  useEffect(() => {
    if ((parseJob as any)?.status === 'completed') {
      toast({
        title: "Scene extraction completed",
        description: "Your script has been divided into scenes. Choose a scene to work with."
      });
      setTimeout(() => {
        setLocation(`/scene-selection/${id}`);
      }, 1000);
    } else if ((parseJob as any)?.status === 'failed') {
      toast({
        title: "Parsing failed",
        description: (parseJob as any)?.errorMessage || "An error occurred during parsing",
        variant: "destructive"
      });
    }
  }, [(parseJob as any)?.status, (parseJob as any)?.errorMessage, id, setLocation, toast]);

  // Start parsing
  const startParsing = () => {
    parseMutation.mutate();
  };

  // Navigate back to columns
  const goBack = () => {
    setLocation(`/columns/${id}`);
  };

  // Show loading state
  if (isLoadingJob || isLoadingScript) {
    return (
      <div className="max-w-3xl mx-auto p-6 flex items-center justify-center min-h-[60vh]">
        <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Show error if job not found
  if (!parseJob) {
    return (
      <div className="max-w-3xl mx-auto p-6">
        <Card>
          <CardContent className="py-12">
            <div className="text-center">
              <h3 className="text-lg font-medium mb-2">Parse job not found</h3>
              <p className="text-muted-foreground mb-4">
                The requested parse job could not be found.
              </p>
              <Button onClick={() => setLocation('/upload')}>
                Go to Upload
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Determine if job is in progress
  const isProcessing = ['pending', 'processing'].includes((parseJob as any)?.status);
  const isCompleted = (parseJob as any)?.status === 'completed';
  const previewData = isCompleted ? ((parseJob as any)?.fullParseData || []) : ((parseJob as any)?.previewData || []);
  const columns = (parseJob as any)?.selectedColumns || [];

  return (
    <div className="max-w-3xl mx-auto p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-semibold mb-1">Parse Script</h2>
        <p className="text-muted-foreground">
          Preview and process your script
        </p>
      </div>

      {/* Script Details */}
      <Card className="mb-8">
        <CardHeader>
          <CardTitle>{(script as any)?.title || "Script details"}</CardTitle>
          <CardDescription>
            {(script as any)?.pageCount} pages ‚Ä¢ {(script as any)?.fileType?.toUpperCase() || "Script"}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-muted-foreground mb-4">
            {(parseJob as any)?.status === 'preview_ready' 
              ? "Preview is ready. You can start the full parse to process the entire script."
              : (parseJob as any)?.status === 'processing'
              ? "Your script is being processed. This may take a few moments."
              : (parseJob as any)?.status === 'completed'
              ? "Parsing completed successfully. You can now review the results."
              : "Prepare to parse your script with the selected columns."}
          </p>

          {(parseJob as any)?.status === 'preview_ready' && (
            <div className="flex justify-end">
              <Button 
                onClick={startParsing}
                disabled={parseMutation.isPending}
              >
                {parseMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Starting...
                  </>
                ) : (
                  <>
                    <Play className="w-4 h-4 mr-2" />
                    Start Full Parse
                  </>
                )}
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Preview Data */}
      <Card className="mb-8">
        <CardHeader>
          <CardTitle>
            {isProcessing 
              ? "Processing..." 
              : (parseJob as any)?.status === 'completed' 
              ? "Parse Completed" 
              : "Script Preview"}
          </CardTitle>
          <CardDescription>
            {isProcessing 
              ? "Your script is being analyzed" 
              : (parseJob as any)?.status === 'completed' 
              ? "Your script has been fully parsed" 
              : "First few scenes from your script"}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isProcessing ? (
            <div className="py-8">
              <div className="flex flex-col items-center justify-center">
                <div className="w-12 h-12 border-4 border-primary border-t-transparent rounded-full animate-spin mb-4"></div>
                <p className="text-muted-foreground">
                  Processing your script. This may take a few moments.
                </p>
              </div>

              <div className="mt-8 space-y-3">
                <Skeleton className="h-10 w-full" />
                <Skeleton className="h-10 w-full" />
                <Skeleton className="h-10 w-full" />
                <Skeleton className="h-10 w-full" />
                <Skeleton className="h-10 w-full" />
              </div>
            </div>
          ) : previewData.length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    {columns.map((column: any) => (
                      <TableHead key={column}>{column.charAt(0).toUpperCase() + column.slice(1)}</TableHead>
                    ))}
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {previewData.map((row: any, index: number) => (
                    <TableRow key={index}>
                      {columns.map((column: any) => (
                        <TableCell key={column}>{row[column] || "-"}</TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="py-8 text-center">
              <p className="text-muted-foreground">No preview data available</p>
            </div>
          )}
        </CardContent>
        {(parseJob as any)?.status === 'completed' && (
          <CardFooter className="flex justify-end">
            <Button asChild>
              <a href={`/api/jobs/${id}/download`}>
                <Download className="mr-2 h-4 w-4" />
                Download Results
              </a>
            </Button>
          </CardFooter>
        )}
      </Card>

      {/* Navigation Buttons */}
      <div className="flex justify-between">
        <Button variant="outline" onClick={goBack}>
          <ArrowLeft className="mr-2 h-5 w-5" />
          Back to Columns
        </Button>

        {(parseJob as any)?.status === 'completed' ? (
          <Button onClick={() => setLocation(`/review/${id}`)}>
            Continue to Review
            <ArrowRight className="ml-2 h-5 w-5" />
          </Button>
        ) : (
          <Button disabled={isProcessing}>
            Processing...
            <ArrowRight className="ml-2 h-5 w-5" />
          </Button>
        )}
      </div>
    </div>
  );
}

client/src/pages/payu-checkout.tsx.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { 
  CreditCard, 
  Shield, 
  CheckCircle, 
  IndianRupee,
  ArrowRight,
  Lock,
  Clock,
  User,
  Mail,
  Phone
} from 'lucide-react';
import { useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useLocation } from 'wouter';

interface PaymentFormData {
  firstname: string;
  email: string;
  phone: string;
  amount: number;
}

export default function PayUCheckout() {
  const [, setLocation] = useLocation();
  const [isProcessing, setIsProcessing] = useState(false);
  const [formData, setFormData] = useState<PaymentFormData>({
    firstname: '',
    email: '',
    phone: '',
    amount: 29
  });

  // Get current user
  const { data: user } = useQuery({
    queryKey: ['/api/auth/user'],
    retry: false
  });

  // Pre-fill form with user data if available
  React.useEffect(() => {
    if (user && typeof user === 'object' && 'email' in user) {
      setFormData(prev => ({
        ...prev,
        firstname: ('displayName' in user ? user.displayName as string : '') || (user.email as string)?.split('@')[0] || '',
        email: user.email as string || ''
      }));
    }
  }, [user]);

  const handleInputChange = (field: keyof PaymentFormData, value: string | number) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handlePayment = async () => {
    if (!user) {
      setLocation('/auth?redirect=payu-checkout');
      return;
    }

    if (!formData.firstname || !formData.email) {
      alert('Please fill in all required fields');
      return;
    }

    setIsProcessing(true);

    try {
      const response = await apiRequest('POST', '/api/payu/create-payment', {
        amount: formData.amount,
        tier: 'pro',
        customerInfo: {
          firstname: formData.firstname,
          email: formData.email,
          phone: formData.phone
        }
      });

      const data = await response.json();
      if (data.success && data.redirectUrl) {
        // Direct redirect to PayU payment gateway
        window.location.href = data.redirectUrl;
      } else {
        throw new Error(data.error || 'Failed to create payment session');
      }
    } catch (error) {
      console.error('Payment initiation failed:', error);
      alert('Payment initiation failed. Please try again.');
      setIsProcessing(false);
    }
  };

  const features = [
    'Unlimited script uploads',
    'Advanced AI shot generation',
    'Storyboard creation',
    'Premium support',
    'Export to professional formats',
    'Priority processing'
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 py-8">
      <div className="container mx-auto px-4 max-w-4xl">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Upgrade to IndieShots Pro
          </h1>
          <p className="text-gray-600">
            Complete your payment securely through PayU gateway
          </p>
        </div>

        <div className="grid lg:grid-cols-2 gap-8">
          {/* Payment Form */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CreditCard className="w-5 h-5" />
                Payment Details
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Customer Information */}
              <div className="space-y-4">
                <h3 className="font-semibold flex items-center gap-2">
                  <User className="w-4 h-4" />
                  Customer Information
                </h3>

                <div className="space-y-3">
                  <div>
                    <Label htmlFor="firstname">Full Name *</Label>
                    <Input
                      id="firstname"
                      value={formData.firstname}
                      onChange={(e) => handleInputChange('firstname', e.target.value)}
                      placeholder="Enter your full name"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <Label htmlFor="email">Email Address *</Label>
                    <Input
                      id="email"
                      type="email"
                      value={formData.email}
                      onChange={(e) => handleInputChange('email', e.target.value)}
                      placeholder="Enter your email"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <Label htmlFor="phone">Phone Number (Optional)</Label>
                    <Input
                      id="phone"
                      value={formData.phone}
                      onChange={(e) => handleInputChange('phone', e.target.value)}
                      placeholder="Enter your phone number"
                      className="mt-1"
                    />
                  </div>
                </div>
              </div>

              <Separator />

              {/* Payment Amount */}
              <div className="space-y-4">
                <h3 className="font-semibold flex items-center gap-2">
                  <IndianRupee className="w-4 h-4" />
                  Payment Amount
                </h3>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <div className="flex justify-between items-center">
                    <span className="text-lg font-medium">IndieShots Pro Subscription</span>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-green-600">
                        ‚Çπ{formData.amount}
                      </div>
                      <div className="text-sm text-gray-500">One-time payment</div>
                    </div>
                  </div>
                </div>
              </div>

              <Separator />

              {/* PayU Security Info */}
              <div className="space-y-3">
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Shield className="w-4 h-4 text-green-500" />
                  <span>Secured by PayU Payment Gateway</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Lock className="w-4 h-4 text-green-500" />
                  <span>SSL encrypted and PCI compliant</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Clock className="w-4 h-4 text-green-500" />
                  <span>Instant activation after payment</span>
                </div>
              </div>

              {/* Payment Button */}
              <Button
                onClick={handlePayment}
                disabled={isProcessing || !formData.firstname || !formData.email}
                className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white py-3 text-lg font-semibold"
              >
                {isProcessing ? (
                  <div className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                    Processing...
                  </div>
                ) : (
                  <div className="flex items-center gap-2">
                    <span>Pay ‚Çπ{formData.amount} via PayU</span>
                    <ArrowRight className="w-4 h-4" />
                  </div>
                )}
              </Button>

              <p className="text-xs text-gray-500 text-center">
                By clicking "Pay via PayU", you agree to our terms of service and will be redirected to PayU's secure payment gateway.
              </p>
            </CardContent>
          </Card>

          {/* Plan Features */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CheckCircle className="w-5 h-5 text-green-500" />
                What's Included
              </CardTitle>
            </CardHeader>
            <CardContent>
