import { useState } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ArrowRight, ArrowLeft, Film, MapPin, Users, Clock } from "lucide-react";
import { cn } from "@/lib/utils";


interface SceneSelectionProps {
  id: string;
}

export default function SceneSelection({ id }: SceneSelectionProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [selectedScene, setSelectedScene] = useState<number | null>(null);

  // Fetch parse job details
  const { data: parseJob, isLoading: isLoadingJob } = useQuery({
    queryKey: [`/api/jobs/${id}`],
  });

  // Fetch associated script
  const { data: script, isLoading: isLoadingScript } = useQuery({
    queryKey: [`/api/scripts/${(parseJob as any)?.scriptId}`],
    enabled: !!(parseJob as any)?.scriptId,
  });

  // Get scenes from the parse job data - try both possible locations
  const scenes = (parseJob as any)?.fullParseData?.scenes || (parseJob as any)?.fullParseData || [];

  // Debug logging
  console.log('Parse job data:', parseJob);
  console.log('Full parse data:', (parseJob as any)?.fullParseData);
  console.log('Scenes found:', scenes);

  const goToShotGeneration = () => {
    if (selectedScene === null) {
      toast({
        title: "No scene selected",
        description: "Please select a scene to generate shots for",
        variant: "destructive",
      });
      return;
    }

    // Navigate directly to shots generation page
    setLocation(`/shots/${id}/${selectedScene}`);
  };

  const goBack = () => {
    setLocation(`/review/${id}`);
  };

  if (isLoadingJob || isLoadingScript) {
    return (
      <div className="max-w-4xl mx-auto p-6">
        <div className="text-center">Loading scenes...</div>
      </div>
    );
  }



  if (!parseJob) {
    return (
      <div className="max-w-4xl mx-auto p-6">
        <Card>
          <CardHeader>
            <CardTitle>Parse Job Not Found</CardTitle>
            <CardDescription>
              The parse job could not be found. Please go back and try again.
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  if (!scenes.length) {
    return (
      <div className="max-w-4xl mx-auto p-6">
        <Card>
          <CardHeader>
            <CardTitle>No Scenes Found</CardTitle>
            <CardDescription>
              No scenes were found in this script. The script may still be processing or failed to parse.
              <br />
              Parse job status: {(parseJob as any)?.status}
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-semibold mb-1">Select Scene for Shot Generation</h2>
        <p className="text-muted-foreground">
          Choose a scene from "{(script as any)?.title}" to generate detailed shots and storyboards
        </p>
      </div>

      {/* Scene Grid */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3 mb-8">
        {scenes.map((scene: any, index: number) => (
          <Card 
            key={index} 
            className={cn(
              "cursor-pointer transition-all hover:shadow-md",
              selectedScene === index && "ring-2 ring-primary bg-primary/5"
            )}
            onClick={() => setSelectedScene(index)}
          >
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <Badge variant="outline" className="text-xs">
                  Scene {scene.sceneNumber || index + 1}
                </Badge>
                {selectedScene === index && (
                  <Badge variant="success" className="text-xs">Selected ‚ú®</Badge>
                )}
              </div>
              <CardTitle className="text-lg leading-tight">
                {scene.sceneHeading || `Scene ${index + 1}`}
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-0">
              <div className="space-y-2">
                {scene.location && (
                  <div className="flex items-center text-sm text-muted-foreground">
                    <MapPin className="h-4 w-4 mr-2" />
                    {scene.location}
                  </div>
                )}

                {scene.time && (
                  <div className="flex items-center text-sm text-muted-foreground">
                    <Clock className="h-4 w-4 mr-2" />
                    {scene.time}
                  </div>
                )}

                {scene.characters && scene.characters.length > 0 && (
                  <div className="flex items-center text-sm text-muted-foreground">
                    <Users className="h-4 w-4 mr-2" />
                    {scene.characters.slice(0, 3).join(", ")}
                    {scene.characters.length > 3 && ` +${scene.characters.length - 3} more`}
                  </div>
                )}

                {scene.action && (
                  <p className="text-sm text-muted-foreground line-clamp-3 mt-2">
                    {scene.action.substring(0, 150)}
                    {scene.action.length > 150 && "..."}
                  </p>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Navigation */}
      <div className="flex justify-between">
        <Button variant="outline" onClick={goBack}>
          <ArrowLeft className="mr-2 h-5 w-5" />
          Back to Review
        </Button>

        <Button 
          onClick={goToShotGeneration}
          disabled={selectedScene === null}
        >
          Generate Shots
          <Film className="ml-2 h-5 w-5" />
        </Button>
      </div>
    </div>
  );
}

client/src/pages/scriptHealth.tsx.content:
import { useState, useEffect } from 'react';
import { useParams, useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  Heart, 
  Target, 
  TrendingUp, 
  Users, 
  MessageCircle, 
  Eye, 
  DollarSign,
  Star,
  CheckCircle,
  AlertTriangle,
  Lightbulb,
  Film,
  Clock,
  Award
} from 'lucide-react';

interface HealthScore {
  overall: number;
  structure: number;
  pacing: number;
  characterDevelopment: number;
  dialogue: number;
  visualStorytelling: number;
  marketability: number;
}

interface ImprovementSuggestion {
  category: string;
  priority: 'high' | 'medium' | 'low';
  suggestion: string;
  example?: string;
  reasoning: string;
}

interface ScriptHealthReport {
  healthScore: HealthScore;
  strengths: string[];
  improvements: ImprovementSuggestion[];
  genre: string;
  mood: string;
  targetAudience: string;
  marketingTags: string[];
  oneLinePitch: string;
  estimatedBudget: string;
  productionComplexity: string;
  lastUpdated?: string;
}

interface ScriptHealthPageProps {
  scriptId: string;
}

export default function ScriptHealthPage({ scriptId }: ScriptHealthPageProps) {
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();

  // Fetch existing health analysis
  const { data: healthData, isLoading } = useQuery({
    queryKey: ['/api/scripts', scriptId, 'health'],
    enabled: !!scriptId,
  });

  // Generate new health analysis
  const generateAnalysis = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/scripts/${scriptId}/health`, {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to generate health analysis');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/scripts', scriptId, 'health'] });
    },
  });

  const analysis: ScriptHealthReport | undefined = (healthData as any)?.analysis;
  const hasAnalysis = !!analysis;

  const getScoreColor = (score: number) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getScoreGrade = (score: number) => {
    if (score >= 90) return 'A+';
    if (score >= 80) return 'A';
    if (score >= 70) return 'B';
    if (score >= 60) return 'C';
    if (score >= 50) return 'D';
    return 'F';
  };

  const getBudgetIcon = (budget: string) => {
    switch (budget) {
      case 'micro': return 'üí∞';
      case 'low': return 'üí∞üí∞';
      case 'medium': return 'üí∞üí∞üí∞';
      case 'high': return 'üí∞üí∞üí∞üí∞';
      default: return 'üí∞';
    }
  };

  const getComplexityIcon = (complexity: string) => {
    switch (complexity) {
      case 'simple': return 'üü¢';
      case 'moderate': return 'üü°';
      case 'complex': return 'üî¥';
      default: return 'üü°';
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'high': return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'medium': return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'low': return <Lightbulb className="h-4 w-4 text-blue-500" />;
      default: return <Lightbulb className="h-4 w-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
            <p className="mt-4 text-muted-foreground">Loading script health analysis...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <Heart className="h-8 w-8 text-red-500" />
              Script Health Score
            </h1>
            <p className="text-muted-foreground mt-2">
              Get AI-powered insights and creative improvement suggestions for your screenplay
            </p>
          </div>
          <div className="flex gap-2">
            <Button
              variant="outline"
              onClick={() => setLocation('/projects')}
            >
              Back to Projects
            </Button>
            <Button
              onClick={() => generateAnalysis.mutate()}
              disabled={generateAnalysis.isPending}
            >
              {generateAnalysis.isPending ? 'Analyzing...' : hasAnalysis ? 'Refresh Analysis' : 'Generate Health Score'}
            </Button>
          </div>
        </div>

        {!hasAnalysis && !generateAnalysis.isPending && (
          <Alert className="mb-8">
            <Star className="h-4 w-4" />
            <AlertDescription>
              Click "Generate Health Score" to get AI-powered creative feedback and improvement suggestions for your script.
            </AlertDescription>
          </Alert>
        )}

        {generateAnalysis.isPending && (
          <Alert className="mb-8">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary"></div>
            <AlertDescription>
              Analyzing your script... This may take a moment as our AI reviews structure, pacing, characters, and more.
            </AlertDescription>
          </Alert>
        )}

        {analysis && (
          <Tabs defaultValue="overview" className="space-y-6">
            <TabsList className="grid w-full grid-cols-4">
              <TabsTrigger value="overview">Overview</TabsTrigger>
              <TabsTrigger value="scores">Detailed Scores</TabsTrigger>
              <TabsTrigger value="improvements">Improvements</TabsTrigger>
              <TabsTrigger value="marketing">Marketing</TabsTrigger>
            </TabsList>

            <TabsContent value="overview" className="space-y-6">
              {/* Overall Score Card */}
              <Card className="text-center">
                <CardHeader>
                  <CardTitle className="text-2xl">Overall Health Score</CardTitle>
                  <CardDescription>Based on structure, pacing, character development, and more</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center justify-center space-x-8">
                    <div className="text-center">
                      <div className={`text-6xl font-bold ${getScoreColor(analysis.healthScore.overall)}`}>
                        {analysis.healthScore.overall}
                      </div>
                      <div className="text-sm text-muted-foreground">out of 100</div>
                    </div>
                    <div className="text-center">
                      <div className={`text-4xl font-bold ${getScoreColor(analysis.healthScore.overall)}`}>
                        {getScoreGrade(analysis.healthScore.overall)}
                      </div>
                      <div className="text-sm text-muted-foreground">Grade</div>
                    </div>
                  </div>
                  <Progress 
                    value={analysis.healthScore.overall} 
                    className="mt-6 h-3"
                  />
                </CardContent>
              </Card>

              {/* Quick Stats */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-muted-foreground">Genre</p>
                        <p className="text-2xl font-bold">{analysis.genre}</p>
                      </div>
                      <Film className="h-8 w-8 text-muted-foreground" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-muted-foreground">Estimated Budget</p>
                        <p className="text-2xl font-bold">{getBudgetIcon(analysis.estimatedBudget)} {analysis.estimatedBudget}</p>
                      </div>
                      <DollarSign className="h-8 w-8 text-muted-foreground" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-muted-foreground">Production Complexity</p>
                        <p className="text-2xl font-bold">{getComplexityIcon(analysis.productionComplexity)} {analysis.productionComplexity}</p>
                      </div>
                      <Target className="h-8 w-8 text-muted-foreground" />
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Strengths */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CheckCircle className="h-5 w-5 text-green-500" />
                    Key Strengths
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {analysis.strengths.map((strength, index) => (
                      <div key={index} className="flex items-start gap-2">
                        <Award className="h-4 w-4 text-green-500 mt-1 flex-shrink-0" />
                        <span className="text-sm">{strength}</span>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* One-Line Pitch */}
              <Card>
                <CardHeader>
                  <CardTitle>One-Line Pitch</CardTitle>
                  <CardDescription>AI-generated marketing description</CardDescription>
                </CardHeader>
                <CardContent>
                  <blockquote className="text-lg italic border-l-4 border-primary pl-4">
                    "{analysis.oneLinePitch}"
                  </blockquote>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="scores" className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {Object.entries(analysis.healthScore).map(([key, score]) => {
                  if (key === 'overall') return null;

                  const icons: Record<string, any> = {
                    structure: Target,
                    pacing: TrendingUp,
                    characterDevelopment: Users,
                    dialogue: MessageCircle,
                    visualStorytelling: Eye,
                    marketability: DollarSign,
                  };

                  const Icon = icons[key] || Star;
                  const displayName = key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase());

                  return (
                    <Card key={key}>
                      <CardContent className="p-6">
                        <div className="flex items-center justify-between mb-4">
                          <div className="flex items-center gap-2">
                            <Icon className="h-5 w-5 text-primary" />
                            <span className="font-medium">{displayName}</span>
                          </div>
                          <div className={`text-2xl font-bold ${getScoreColor(score)}`}>
                            {score}
                          </div>
                        </div>
                        <Progress value={score} className="h-2" />
                        <div className="mt-2 text-sm text-muted-foreground">
                          Grade: {getScoreGrade(score)}
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            </TabsContent>

            <TabsContent value="improvements" className="space-y-6">
              <div className="space-y-4">
                {analysis.improvements.map((improvement, index) => (
                  <Card key={index}>
                    <CardHeader>
                      <div className="flex items-center justify-between">
                        <CardTitle className="flex items-center gap-2">
                          {getPriorityIcon(improvement.priority)}
                          {improvement.category}
                        </CardTitle>
                        <Badge variant={improvement.priority === 'high' ? 'destructive' : improvement.priority === 'medium' ? 'default' : 'secondary'}>
                          {improvement.priority} priority
                        </Badge>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <p className="text-sm mb-4">{improvement.suggestion}</p>
                      {improvement.example && (
                        <div className="bg-muted p-3 rounded-md mb-4">
                          <p className="text-sm font-medium mb-1">Example:</p>
                          <p className="text-sm text-muted-foreground">{improvement.example}</p>
                        </div>
                      )}
                      <div className="bg-blue-50 p-3 rounded-md">
                        <p className="text-sm font-medium mb-1">Why this helps:</p>
                        <p className="text-sm text-blue-700">{improvement.reasoning}</p>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </TabsContent>

            <TabsContent value="marketing" className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <Card>
                  <CardHeader>
                    <CardTitle>Target Audience</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p className="text-lg">{analysis.targetAudience}</p>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Mood & Tone</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p className="text-lg">{analysis.mood}</p>
                  </CardContent>
                </Card>
              </div>

              <Card>
                <CardHeader>
                  <CardTitle>Marketing Tags</CardTitle>
                  <CardDescription>Keywords for promoting your script</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex flex-wrap gap-2">
                    {analysis.marketingTags.map((tag, index) => (
                      <Badge key={index} variant="outline">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Production Details</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <p className="text-sm font-medium text-muted-foreground mb-2">Estimated Budget</p>
                      <p className="text-lg">{getBudgetIcon(analysis.estimatedBudget)} {analysis.estimatedBudget.charAt(0).toUpperCase() + analysis.estimatedBudget.slice(1)} Budget</p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-muted-foreground mb-2">Production Complexity</p>
                      <p className="text-lg">{getComplexityIcon(analysis.productionComplexity)} {analysis.productionComplexity.charAt(0).toUpperCase() + analysis.productionComplexity.slice(1)} Production</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        )}
      </div>
    </div>
  );
}

