import * as React from "react";
import { useState, useRef } from "react";
import { cn, isValidFileType, isValidFileSize, formatFileSize } from "@/lib/utils";
import { Upload, FileWarning, FileX } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface FileUploadProps extends React.HTMLAttributes<HTMLDivElement> {
  onFileChange: (file: File | null) => void;
  onTextChange: (text: string) => void;
  maxSize?: number; // in bytes
  acceptedFileTypes?: string[];
  defaultText?: string;
  pageCount?: number;
  userTier?: string;
  totalPages?: number;
}

export function FileUpload({
  className,
  onFileChange,
  onTextChange,
  maxSize = 10 * 1024 * 1024, // 10MB default (increased for PDF support)
  acceptedFileTypes = [".pdf", ".docx", ".txt"],
  defaultText = "",
  pageCount = 0,
  userTier = 'free',
  totalPages = 5,
  ...props
}: FileUploadProps) {
  const [dragActive, setDragActive] = useState(false);
  const [file, setFile] = useState<File | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [scriptText, setScriptText] = useState(defaultText);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleDrag = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const validateFile = (file: File): boolean => {
    // Check file size
    if (file.size > maxSize) {
      setError(`File size is too large. Maximum allowed is ${formatFileSize(maxSize)}.`);
      toast({
        title: "File too large",
        description: `Maximum allowed size is ${formatFileSize(maxSize)}.`,
        variant: "destructive",
      });
      return false;
    }

    // Check file type
    const fileExt = `.${file.name.split(".").pop()?.toLowerCase()}`;
    if (!acceptedFileTypes.includes(fileExt)) {
      setError(`Invalid file type. Accepted types: ${acceptedFileTypes.join(", ")}`);
      toast({
        title: "Invalid file type",
        description: `Accepted types: ${acceptedFileTypes.join(", ")}`,
        variant: "destructive",
      });
      return false;
    }

    setError(null);
    return true;
  };

  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const uploadedFile = e.dataTransfer.files[0];
      if (validateFile(uploadedFile)) {
        setFile(uploadedFile);
        onFileChange(uploadedFile);

        // Clear textarea when file is uploaded
        setScriptText("");
        onTextChange("");
      }
    }
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const uploadedFile = e.target.files[0];
      if (validateFile(uploadedFile)) {
        setFile(uploadedFile);
        onFileChange(uploadedFile);

        // Clear textarea when file is uploaded
        setScriptText("");
        onTextChange("");
      }
    }
  };

  const handleTextareaChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setScriptText(e.target.value);
    onTextChange(e.target.value);

    // Clear file when text is entered
    if (e.target.value && file) {
      setFile(null);
      onFileChange(null);
    }
  };

  // Calculate page count from text
  const estimatedPages = Math.ceil(scriptText.split(/\s+/).filter(word => word.length > 0).length / 250);

  const handleButtonClick = () => {
    fileInputRef.current?.click();
  };

  const removeFile = () => {
    setFile(null);
    onFileChange(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className={cn("space-y-6", className)} {...props}>
      {/* File Upload Area */}
      <div
        className={cn(
          "border-2 border-dashed rounded-lg p-8 text-center transition-colors",
          dragActive ? "border-primary bg-muted/50" : "border-border",
          error ? "border-destructive" : ""
        )}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        <input
          ref={fileInputRef}
          type="file"
          className="hidden"
          accept={acceptedFileTypes.join(",")}
          onChange={handleFileInput}
        />

        {error ? (
          <div className="flex flex-col items-center justify-center space-y-3">
            <FileWarning className="h-10 w-10 text-destructive" />
            <p className="text-destructive">{error}</p>
            <Button variant="outline" onClick={() => setError(null)}>
              Try Again
            </Button>
          </div>
        ) : file ? (
          <div className="flex flex-col items-center justify-center space-y-3">
            <div className="flex items-center justify-center w-16 h-16 rounded-full bg-primary/10">
              <Upload className="h-8 w-8 text-primary" />
            </div>
            <div>
              <p className="text-lg font-medium">{file.name}</p>
              <p className="text-sm text-muted-foreground">
                {formatFileSize(file.size)}
              </p>
            </div>
            <Button variant="outline" size="sm" onClick={removeFile}>
              <FileX className="h-4 w-4 mr-2" />
              Remove File
            </Button>
          </div>
        ) : (
          <>
            <div className="flex flex-col items-center justify-center space-y-3 mb-4">
              <div className="flex items-center justify-center w-16 h-16 rounded-full bg-primary/10">
                <Upload className="h-8 w-8 text-primary" />
              </div>
              <h3 className="text-lg font-medium">Drag and drop your script file</h3>
              <p className="text-sm text-muted-foreground max-w-md mx-auto">
                Supported formats: PDF, DOCX, TXT (max 5MB)
              </p>
            </div>

            <Button onClick={handleButtonClick} className="mb-4">
              <Upload className="h-5 w-5 mr-2" />
              Browse Files
            </Button>
          </>
        )}
      </div>

      {/* Or Divider */}
      <div className="flex items-center">
        <div className="flex-1 border-t border-border"></div>
        <div className="px-4 text-sm text-muted-foreground">OR</div>
        <div className="flex-1 border-t border-border"></div>
      </div>

      {/* Paste Script Area */}
      <div>
        <label
          htmlFor="script"
          className="block text-sm font-medium mb-2"
        >
          Paste your script here
        </label>
        <div className="relative">
          <Textarea
            id="script"
            rows={12}
            className="font-mono resize-none"
            placeholder="INT. COFFEE SHOP - DAY&#10;&#10;A busy caf√© filled with CUSTOMERS. JANE (30s, professional) sits alone at a corner table, working on her laptop.&#10;&#10;JOHN (30s, casual) enters, looks around, and approaches the counter."
            value={scriptText}
            onChange={handleTextareaChange}
          />
          <div className="absolute right-2 bottom-2 text-xs bg-background px-2 py-1 rounded text-muted-foreground">
            <span>{estimatedPages || 0}</span> pages (approx.)
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/film-animations.content:
/* Film Industry Themed Animations */

/* Common Container Styles */
.film-loading-container,
.film-error-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 300px;
  padding: 2rem;
  background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%);
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  position: relative;
  overflow: hidden;
}

.film-loading-container::before,
.film-error-container::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    repeating-linear-gradient(
      90deg,
      transparent,
      transparent 2px,
      rgba(255, 255, 255, 0.03) 2px,
      rgba(255, 255, 255, 0.03) 4px
    );
  pointer-events: none;
  animation: filmGrain 0.2s infinite;
}

@keyframes filmGrain {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.8; }
}

/* ===============================
   CLAPPERBOARD LOADING ANIMATION
   =============================== */

.clapperboard-wrapper {
  position: relative;
  width: 200px;
  height: 160px;
  margin-bottom: 2rem;
}

.clapperboard-base {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 120px;
  background: #2c2c2c;
  border-radius: 8px;
  border: 3px solid #444;
  overflow: hidden;
  z-index: 1;
}

.clapperboard-top {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background: #2c2c2c;
  border-radius: 8px 8px 0 0;
  border: 3px solid #444;
  border-bottom: none;
  overflow: hidden;
  transform-origin: bottom center;
  z-index: 2;
  animation: clapperboardClap 2s infinite;
}

@keyframes clapperboardClap {
  0%, 20% { transform: rotateX(0deg); }
  10% { transform: rotateX(-45deg); }
  30%, 100% { transform: rotateX(0deg); }
}

.clapperboard-stripes {
  display: flex;
  width: 100%;
  height: 100%;
}

.stripe {
  flex: 1;
  height: 100%;
}

.stripe.black { background: #000; }
.stripe.white { background: #fff; }

.film-info {
  position: absolute;
  bottom: 10px;
  left: 10px;
  right: 10px;
  color: #fff;
  font-family: 'Courier New', monospace;
  font-size: 10px;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
}

.film-title {
  font-weight: bold;
  font-size: 12px;
  margin-bottom: 4px;
}

.film-details {
  display: flex;
  justify-content: space-between;
  font-size: 8px;
  opacity: 0.9;
}

.clapperboard-hinge {
  position: absolute;
  top: 55px;
  left: 50%;
  transform: translateX(-50%);
  width: 20px;
  height: 10px;
  background: #666;
  border-radius: 5px;
  box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.3);
  z-index: 3;
}

/* ===============================
   SECURITY GUARD ANIMATION
   =============================== */

.security-guard {
  display: flex;
  align-items: center;
  gap: 2rem;
  margin-bottom: 2rem;
}

.guard-body {
  position: relative;
  animation: guardShakeHead 2s infinite;
}

@keyframes guardShakeHead {
  0%, 20%, 40%, 60%, 80%, 100% { transform: rotate(0deg); }
  10%, 30%, 50%, 70%, 90% { transform: rotate(-5deg); }
}

.guard-head {
  position: relative;
  width: 60px;
  height: 60px;
  background: #d4a574;
  border-radius: 50%;
  margin-bottom: 10px;
}

.guard-face {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.guard-eyes {
  display: flex;
  gap: 10px;
  margin-bottom: 8px;
}

.eye {
  width: 6px;
  height: 6px;
  background: #333;
  border-radius: 50%;
  animation: guardBlink 3s infinite;
}

@keyframes guardBlink {
  0%, 5%, 100% { transform: scaleY(1); }
  2.5% { transform: scaleY(0.1); }
}

.guard-mouth {
  width: 8px;
  height: 4px;
  background: #333;
  border-radius: 0 0 8px 8px;
  transform: rotate(180deg);
}

.guard-hat {
  position: absolute;
  top: -10px;
  left: 50%;
  transform: translateX(-50%);
  width: 50px;
  height: 20px;
  background: #1a1a1a;
  border-radius: 25px 25px 0 0;
  border: 2px solid #333;
}

.guard-torso {
  width: 80px;
  height: 80px;
  background: #1a1a1a;
  border-radius: 10px;
  margin-bottom: 10px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.guard-badge {
  font-size: 24px;
  animation: badgeShine 2s infinite;
}

@keyframes badgeShine {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

.access-denied-sign {
  position: relative;
  animation: signSway 3s infinite;
}

@keyframes signSway {
  0%, 100% { transform: rotate(0deg); }
  25% { transform: rotate(-2deg); }
  75% { transform: rotate(2deg); }
}

.sign-post {
  width: 6px;
  height: 80px;
  background: #8b4513;
  margin: 0 auto;
  border-radius: 3px;
}

.sign-board {
  width: 100px;
  height: 60px;
  background: #ff4444;
  border: 3px solid #cc0000;
  border-radius: 8px;
  position: relative;
  top: -10px;
  display: flex;
  align-items: center;
  justify-content: center;
  animation: signFlash 1s infinite;
}

@keyframes signFlash {
  0%, 50%, 100% { background: #ff4444; }
  25%, 75% { background: #ff6666; }
}

.sign-text {
  text-align: center;
  color: white;
  font-weight: bold;
  font-size: 10px;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
}

.denied-icon {
  display: block;
  font-size: 20px;
  margin-bottom: 4px;
}

/* ===============================
   PROJECTOR ERROR ANIMATION
   =============================== */

.projector-setup {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin-bottom: 2rem;
}

.projector {
  position: relative;
  width: 200px;
  height: 100px;
}

.projector-body {
  width: 100%;
  height: 60px;
  background: #333;
  border-radius: 12px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}

.projector-lens {
  width: 40px;
  height: 40px;
  background: radial-gradient(circle, #666 0%, #333 100%);
  border-radius: 50%;
  border: 4px solid #222;
  position: relative;
  animation: projectorFlicker 2s infinite;
}

@keyframes projectorFlicker {
  0%, 100% { box-shadow: 0 0 10px #666; }
  50% { box-shadow: 0 0 20px #999; }
}

.projector-reel {
  width: 30px;
  height: 30px;
  background: #444;
  border-radius: 50%;
  position: relative;
  animation: reelSpin 3s linear infinite;
}

@keyframes reelSpin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.reel-center {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 8px;
  height: 8px;
  background: #222;
  border-radius: 50%;
}

.projection-screen {
  width: 200px;
  height: 120px;
  background: #f0f0f0;
  border: 8px solid #222;
  border-radius: 8px;
  position: relative;
  overflow: hidden;
  animation: screenFlicker 1s infinite;
}

@keyframes screenFlicker {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.9; }
}

.screen-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  width: 100%;
}

.no-signal-text {
  color: #333;
  font-weight: bold;
  font-family: 'Courier New', monospace;
}

.signal-icon {
  display: block;
  font-size: 40px;
  margin-bottom: 10px;
  animation: signalBlink 0.5s infinite;
}

@keyframes signalBlink {
  0%, 50% { opacity: 1; }
  51%, 100% { opacity: 0.5; }
}

.signal-text {
  font-size: 14px;
  letter-spacing: 2px;
}

.static-lines {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    repeating-linear-gradient(
      0deg,
      transparent,
      transparent 2px,
      rgba(0, 0, 0, 0.1) 2px,
      rgba(0, 0, 0, 0.1) 4px
    );
  animation: staticMove 0.1s infinite;
}

@keyframes staticMove {
  0% { transform: translateY(0); }
  100% { transform: translateY(-4px); }
}

/* ===============================
   BUFFERING/NETWORK LAG ANIMATION
   =============================== */

.buffering-setup {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin-bottom: 2rem;
}

.film-reel {
  position: relative;
  width: 120px;
  height: 120px;
  animation: reelSlowSpin 4s linear infinite;
}

@keyframes reelSlowSpin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.reel-outer {
  width: 100%;
  height: 100%;
  background: #333;
  border-radius: 50%;
  border: 4px solid #222;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.reel-inner {
  width: 80px;
  height: 80px;
  background: #444;
  border-radius: 50%;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.reel-center {
  width: 20px;
  height: 20px;
  background: #222;
  border-radius: 50%;
}

.reel-holes {
  position: absolute;
  width: 100%;
  height: 100%;
}

.hole {
  position: absolute;
  width: 6px;
  height: 6px;
  background: #222;
  border-radius: 50%;
}

.hole:nth-child(1) { top: 10px; left: 50%; transform: translateX(-50%); }
.hole:nth-child(2) { top: 50%; right: 10px; transform: translateY(-50%); }
.hole:nth-child(3) { bottom: 10px; left: 50%; transform: translateX(-50%); }
.hole:nth-child(4) { top: 50%; left: 10px; transform: translateY(-50%); }
.hole:nth-child(5) { top: 25%; right: 25%; }
.hole:nth-child(6) { bottom: 25%; left: 25%; }

.popcorn-container {
  position: relative;
  width: 200px;
  height: 80px;
}

.popcorn {
  position: absolute;
  font-size: 24px;
  animation: popcornBounce 2s infinite;
}

.popcorn.piece1 {
  left: 20px;
  animation-delay: 0s;
}

.popcorn.piece2 {
  left: 80px;
  animation-delay: 0.5s;
}

.popcorn.piece3 {
  left: 140px;
  animation-delay: 1s;
}

.popcorn.piece4 {
  left: 60px;
  animation-delay: 1.5s;
}

@keyframes popcornBounce {
  0%, 100% { transform: translateY(0) rotate(0deg); }
  25% { transform: translateY(-20px) rotate(-10deg); }
  50% { transform: translateY(-40px) rotate(0deg); }
  75% { transform: translateY(-20px) rotate(10deg); }
}

/* ===============================
   LOADING TEXT AND PROGRESS BARS
   =============================== */

.loading-text {
  text-align: center;
  color: #fff;
  font-family: 'Courier New', monospace;
}

.loading-message {
  font-size: 18px;
  font-weight: bold;
  display: block;
  margin-bottom: 1rem;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
}

.loading-dots {
  display: flex;
  justify-content: center;
  gap: 4px;
}

.loading-dots span {
  width: 8px;
  height: 8px;
  background: #fff;
  border-radius: 50%;
  animation: loadingDots 1.5s infinite;
}

.loading-dots span:nth-child(1) { animation-delay: 0s; }
.loading-dots span:nth-child(2) { animation-delay: 0.5s; }
.loading-dots span:nth-child(3) { animation-delay: 1s; }

@keyframes loadingDots {
  0%, 100% { opacity: 0.3; transform: scale(0.8); }
  50% { opacity: 1; transform: scale(1.2); }
}

.buffering-bar {
  width: 200px;
  height: 6px;
  background: #333;
  border-radius: 3px;
  overflow: hidden;
  margin-top: 1rem;
  position: relative;
}

.buffering-progress {
  height: 100%;
  background: linear-gradient(90deg, #ff6b6b, #4ecdc4);
  border-radius: 3px;
  animation: bufferingProgress 3s infinite;
}

@keyframes bufferingProgress {
  0% { width: 0%; }
  50% { width: 70%; }
  100% { width: 100%; }
}

.error-message {
  text-align: center;
  color: #ff6b6b;
  font-family: 'Courier New', monospace;
  font-size: 16px;
  font-weight: bold;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
  margin-top: 1rem;
}

/* ===============================
   RESPONSIVE DESIGN
   =============================== */

@media (max-width: 640px) {
  .film-loading-container,
  .film-error-container {
    padding: 1rem;
    min-height: 250px;
  }

  .clapperboard-wrapper {
    width: 160px;
    height: 130px;
  }

  .loading-message {
    font-size: 16px;
  }

  .security-guard {
    flex-direction: column;
    gap: 1rem;
  }

  .projector-setup {
    gap: 1rem;
  }

  .projection-screen {
    width: 160px;
    height: 100px;
  }
}

