import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { 
  Users, 
  Calendar, 
  TrendingUp, 
  Clock,
  CheckCircle,
  XCircle,
  Refresh
} from 'lucide-react';

export default function PromoCodesAdminPage() {
  const [selectedCode] = useState('INDIE2025');

  // Query promo code stats
  const { data: stats, isLoading: statsLoading, refetch: refetchStats } = useQuery({
    queryKey: [`/api/promo-codes/${selectedCode}/stats`],
    refetchInterval: 30000,
  });

  // Query promo code status
  const { data: status, isLoading: statusLoading, refetch: refetchStatus } = useQuery({
    queryKey: ['/api/promo-codes/status'],
    refetchInterval: 60000,
  });

  const handleRefreshData = () => {
    refetchStats();
    refetchStatus();
  };

  const getUsagePercentage = () => {
    if (!stats || stats.stats.usageLimit === -1) return 0;
    return (stats.stats.totalUses / stats.stats.usageLimit) * 100;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (statsLoading || statusLoading) {
    return (
      <div className="container mx-auto py-8">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-muted rounded w-1/3"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {[1, 2, 3, 4].map(i => (
              <div key={i} className="h-24 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-8 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Promo Code Analytics</h1>
          <p className="text-muted-foreground mt-1">
            Monitor promo code usage and system status
          </p>
        </div>
        <Button onClick={handleRefreshData} variant="outline" size="sm">
          <Refresh className="h-4 w-4 mr-2" />
          Refresh Data
        </Button>
      </div>

      {/* Current Status */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            System Status
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4">
            {status?.isValidDate ? (
              <CheckCircle className="h-5 w-5 text-green-600" />
            ) : (
              <XCircle className="h-5 w-5 text-red-600" />
            )}
            <div>
              <p className="font-medium">{status?.message}</p>
              <p className="text-sm text-muted-foreground">
                Current Date: {status?.currentDate}
              </p>
              <p className="text-sm text-muted-foreground">
                Valid Dates: {status?.validDates?.join(', ')}
              </p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Stats Overview */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Uses</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.stats.totalUses}</div>
              <p className="text-xs text-muted-foreground">
                {stats.stats.usageLimit === -1 ? 'Unlimited' : `of ${stats.stats.usageLimit}`}
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Unique Users</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.stats.uniqueUsers}</div>
              <p className="text-xs text-muted-foreground">
                Individual accounts
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Remaining Uses</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {stats.stats.remainingUses === -1 ? '‚àû' : stats.stats.remainingUses}
              </div>
              <p className="text-xs text-muted-foreground">
                Available slots
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Usage Progress</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{Math.round(getUsagePercentage())}%</div>
              <Progress value={getUsagePercentage()} className="mt-2" />
            </CardContent>
          </Card>
        </div>
      )}

      {/* Recent Usage */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Applications</CardTitle>
          <CardDescription>
            Latest promo code redemptions for {selectedCode}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {stats?.stats.recentUsage?.length > 0 ? (
            <div className="space-y-3">
              {stats.stats.recentUsage.map((usage: any, index: number) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                  <div className="space-y-1">
                    <p className="font-medium">{usage.email}</p>
                    <p className="text-sm text-muted-foreground">
                      {formatDateTime(usage.usedAt.toString())}
                    </p>
                  </div>
                  <div className="text-right">
                    <Badge variant="outline">{usage.ipAddress}</Badge>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              No recent usage found
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

server/services/costController.ts.content:
/**
 * Cost Controller - Prevents excessive OpenAI API usage and billing
 */

interface UsageTracker {
  imageGenerations: number;
  gptCalls: number;
  lastReset: Date;
  totalCost: number;
}

class CostController {
  private static instance: CostController;
  private userUsage: Map<string, UsageTracker> = new Map();

  // Daily limits to prevent billing spikes
  private readonly DAILY_LIMITS = {
    FREE_TIER: {
      imageGenerations: 5,   // 5 images per day ($0.20-0.40)
      gptCalls: 20,          // 20 GPT calls per day (~$0.10)
      maxCostPerDay: 0.50    // $0.50 maximum per day
    },
    PRO_TIER: {
      imageGenerations: 50,  // 50 images per day ($2.00-4.00)
      gptCalls: 200,         // 200 GPT calls per day (~$1.00)
      maxCostPerDay: 5.00    // $5.00 maximum per day
    }
  };

  private constructor() {
    // Reset usage counters daily
    setInterval(() => {
      this.resetDailyUsage();
    }, 24 * 60 * 60 * 1000); // 24 hours
  }

  static getInstance(): CostController {
    if (!CostController.instance) {
      CostController.instance = new CostController();
    }
    return CostController.instance;
  }

  private resetDailyUsage(): void {
    const now = new Date();
    for (const [userId, usage] of this.userUsage.entries()) {
      const hoursSinceReset = (now.getTime() - usage.lastReset.getTime()) / (1000 * 60 * 60);
      if (hoursSinceReset >= 24) {
        this.userUsage.set(userId, {
          imageGenerations: 0,
          gptCalls: 0,
          lastReset: now,
          totalCost: 0
        });
      }
    }
  }

  private getUserUsage(userId: string): UsageTracker {
    if (!this.userUsage.has(userId)) {
      this.userUsage.set(userId, {
        imageGenerations: 0,
        gptCalls: 0,
        lastReset: new Date(),
        totalCost: 0
      });
    }
    return this.userUsage.get(userId)!;
  }

  /**
   * Check if user can generate images without exceeding cost limits
   */
  canGenerateImage(userId: string, userTier: string = 'free'): { allowed: boolean; reason?: string } {
    const usage = this.getUserUsage(userId);
    const limits = userTier === 'pro' ? this.DAILY_LIMITS.PRO_TIER : this.DAILY_LIMITS.FREE_TIER;

    // Check image generation limit
    if (usage.imageGenerations >= limits.imageGenerations) {
      return {
        allowed: false,
        reason: `Daily image generation limit reached (${limits.imageGenerations}). Resets in 24 hours.`
      };
    }

    // Check cost limit
    const estimatedCost = 0.08; // DALL-E 3 cost per image
    if (usage.totalCost + estimatedCost > limits.maxCostPerDay) {
      return {
        allowed: false,
        reason: `Daily cost limit reached ($${limits.maxCostPerDay}). Resets in 24 hours.`
      };
    }

    return { allowed: true };
  }

  /**
   * Check if user can make GPT calls without exceeding limits
   */
  canMakeGPTCall(userId: string, userTier: string = 'free'): { allowed: boolean; reason?: string } {
    const usage = this.getUserUsage(userId);
    const limits = userTier === 'pro' ? this.DAILY_LIMITS.PRO_TIER : this.DAILY_LIMITS.FREE_TIER;

    if (usage.gptCalls >= limits.gptCalls) {
      return {
        allowed: false,
        reason: `Daily GPT call limit reached (${limits.gptCalls}). Resets in 24 hours.`
      };
    }

    return { allowed: true };
  }

  /**
   * Record image generation usage
   */
  recordImageGeneration(userId: string, cost: number = 0.08): void {
    const usage = this.getUserUsage(userId);
    usage.imageGenerations += 1;
    usage.totalCost += cost;

    console.log(`üîí COST TRACKING: User ${userId} - Images: ${usage.imageGenerations}, Cost: $${usage.totalCost.toFixed(2)}`);
  }

  /**
   * Record GPT call usage
   */
  recordGPTCall(userId: string, cost: number = 0.005): void {
    const usage = this.getUserUsage(userId);
    usage.gptCalls += 1;
    usage.totalCost += cost;

    console.log(`üîí COST TRACKING: User ${userId} - GPT Calls: ${usage.gptCalls}, Cost: $${usage.totalCost.toFixed(2)}`);
  }

  /**
   * Get current usage for a user
   */
  getUserUsageStats(userId: string): UsageTracker {
    return this.getUserUsage(userId);
  }
}

export const costController = CostController.getInstance();

