        {/* Last Reset Time */}
        <div className="flex items-center gap-2 text-xs text-gray-500">
          <Clock className="w-4 h-4" />
          Last reset: {new Date(usage.lastReset).toLocaleString()}
        </div>
      </CardContent>
    </Card>
  );
}

