// Central payment service that handles both PayU and Stripe integrations
import { StripeService } from './stripeService';
import { PayuService } from './payuService';
import { EmailService } from './emailService';
import { storage } from '../storage';
import type { User } from '@shared/schema';

export interface PaymentMethodInfo {
  id: string;
  name: string;
  description: string;
  currencies: string[];
  regions: string[];
  available: boolean;
}

export interface CreatePaymentSessionRequest {
  method: string;
  amount: number;
  currency: string;
  userId: string;
  userEmail: string;
  userDisplayName?: string;
}

export interface CreatePaymentSessionResponse {
  success: boolean;
  url?: string;
  redirectUrl?: string;
  sessionId?: string;
  error?: string;
}

export class PaymentService {
  private stripeService?: StripeService;
  private payuService: PayuService;
  private emailService: EmailService;

  constructor() {
    // Initialize PayU (always available)
    this.payuService = new PayuService();

    // Initialize Stripe (optional in development)
    try {
      this.stripeService = new StripeService();
    } catch (error) {
      console.warn('Stripe service unavailable:', error.message);
    }

    // Initialize email service
    this.emailService = new EmailService();
  }

  /**
   * Get available payment methods
   */
  getAvailablePaymentMethods(): PaymentMethodInfo[] {
    const methods: PaymentMethodInfo[] = [];

    // PayU method
    methods.push({
      id: 'payu',
      name: 'PayU',
      description: 'Secure payments for India - Credit/Debit Cards, Net Banking, UPI',
      currencies: ['inr'],
      regions: ['India'],
      available: true
    });

    // Stripe method (if available)
    if (this.stripeService) {
      methods.push({
        id: 'stripe',
        name: 'Stripe',
        description: 'Global payments - Credit/Debit Cards, Apple Pay, Google Pay',
        currencies: ['usd', 'eur', 'gbp'],
        regions: ['global'],
        available: true
      });
    }

    return methods;
  }

  /**
   * Auto-detect best payment method based on currency
   */
  getRecommendedPaymentMethod(currency: string): string {
    if (currency.toLowerCase() === 'inr') {
      return 'payu';
    }

    if (this.stripeService && ['usd', 'eur', 'gbp'].includes(currency.toLowerCase())) {
      return 'stripe';
    }

    // Default fallback
    return this.stripeService ? 'stripe' : 'payu';
  }

  /**
   * Create payment session with appropriate provider
   */
  async createPaymentSession(request: CreatePaymentSessionRequest): Promise<CreatePaymentSessionResponse> {
    try {
      const { method, amount, currency, userId, userEmail, userDisplayName } = request;

      // Auto-detect method if 'auto' is specified
      const actualMethod = method === 'auto' 
        ? this.getRecommendedPaymentMethod(currency)
        : method;

      console.log(`Creating payment session: ${actualMethod}, ${amount} ${currency.toUpperCase()} for ${userEmail}`);

      if (actualMethod === 'stripe' && this.stripeService) {
        // Create Stripe session
        const session = await this.stripeService.createCheckoutSession({
          amount,
          currency,
          customerEmail: userEmail,
          customerName: userDisplayName,
          tier: 'pro',
          userId
        });

        return {
          success: true,
          url: session.url || undefined,
          sessionId: session.id
        };

      } else if (actualMethod === 'payu') {
        // Create PayU session
        const payuResult = await this.payuService.createPaymentSession({
          amount,
          currency: 'INR', // PayU only supports INR
          customerEmail: userEmail,
          customerName: userDisplayName || 'IndieShots User',
          tier: 'pro',
          userId
        });

        return {
          success: true,
          redirectUrl: payuResult.redirectUrl,
          sessionId: payuResult.txnId
        };

      } else {
        return {
          success: false,
          error: `Payment method ${actualMethod} is not available`
        };
      }

    } catch (error) {
      console.error('Payment session creation failed:', error);
      return {
        success: false,
        error: error.message || 'Failed to create payment session'
      };
    }
  }

  /**
   * Handle successful payment and upgrade user
   */
  async handleSuccessfulPayment(userId: string, paymentInfo: any): Promise<void> {
    try {
      // Update user tier to pro
      const paymentData = {
        paymentMethod: paymentInfo.method,
        paymentStatus: 'active',
        ...(paymentInfo.stripeCustomerId && { stripeCustomerId: paymentInfo.stripeCustomerId }),
        ...(paymentInfo.stripeSubscriptionId && { stripeSubscriptionId: paymentInfo.stripeSubscriptionId }),
        ...(paymentInfo.payuTransactionId && { payuTransactionId: paymentInfo.payuTransactionId }),
        ...(paymentInfo.payuTxnId && { payuTxnId: paymentInfo.payuTxnId })
      };

      await storage.updateUserTier(userId, 'pro', paymentData);

      // Send confirmation email
      const user = await storage.getUserByProviderId(userId);
      if (user?.email) {
        await this.emailService.sendPaymentConfirmation({
          email: user.email,
          name: user.displayName || user.firstName || 'IndieShots User',
          amount: paymentInfo.amount || 29,
          currency: paymentInfo.currency || 'USD',
          method: paymentInfo.method,
          transactionId: paymentInfo.transactionId
        });

        // Send welcome email for new Pro users
        await this.emailService.sendWelcomeEmail({
          email: user.email,
          name: user.displayName || user.firstName || 'IndieShots User'
        });
      }

      console.log(`‚úÖ Payment processed successfully for user ${userId}`);

    } catch (error) {
      console.error('Failed to handle successful payment:', error);
      throw error;
    }
  }

  /**
   * Get payment environment info
   */
  getEnvironmentInfo() {
    return {
      environment: process.env.NODE_ENV || 'development',
      payuMode: process.env.NODE_ENV === 'production' ? 'live' : 'test',
      stripeMode: process.env.NODE_ENV === 'production' ? 'live' : 'test',
      stripeAvailable: !!this.stripeService,
      payuAvailable: true
    };
  }
}

// Export singleton instance
export const paymentService = new PaymentService();

server/services/stripeService.ts.content:
import Stripe from 'stripe';

export interface StripeConfig {
  secretKey: string;
  publishableKey: string;
  webhookSecret: string;
  successUrl: string;
  cancelUrl: string;
}

export interface CheckoutSessionData {
  priceId?: string;
  amount: number;
  currency: string;
  customerEmail: string;
  customerName?: string;
  tier: string;
  userId: string;
}

export class StripeService {
  private stripe: Stripe;
  private config: StripeConfig;

  constructor() {
    const secretKey = process.env.STRIPE_SECRET_KEY;
    if (!secretKey) {
      // In development mode, provide a warning but don't crash
      if (process.env.NODE_ENV !== 'production') {
        console.warn('‚ö†Ô∏è  STRIPE_SECRET_KEY not found - Stripe payments will not work');
        this.stripe = null as any;
        this.config = {} as StripeConfig;
        return;
      }
      throw new Error('STRIPE_SECRET_KEY environment variable is required');
    }

    this.stripe = new Stripe(secretKey, {
      apiVersion: '2025-05-28.basil',
    });

    const baseUrl = process.env.NODE_ENV === 'production' 
      ? process.env.BASE_URL || 'https://indieshots.onrender.com'
      : 'http://localhost:5000';

    this.config = {
      secretKey,
      publishableKey: process.env.STRIPE_PUBLISHABLE_KEY || '',
      webhookSecret: process.env.STRIPE_WEBHOOK_SECRET || '',
      successUrl: `${baseUrl}/payment/success`,
      cancelUrl: `${baseUrl}/payment/cancel`
    };

    console.log('Stripe Service initialized');
    console.log(`Success URL: ${this.config.successUrl}`);
    console.log(`Cancel URL: ${this.config.cancelUrl}`);
  }

  /**
   * Create Stripe checkout session
   */
  async createCheckoutSession(data: CheckoutSessionData): Promise<Stripe.Checkout.Session> {
    try {
      const sessionParams: Stripe.Checkout.SessionCreateParams = {
        payment_method_types: ['card'],
        line_items: [
          {
            price_data: {
              currency: data.currency,
              product_data: {
                name: `IndieShots ${data.tier.charAt(0).toUpperCase() + data.tier.slice(1)} Plan`,
                description: data.tier === 'pro' 
                  ? 'Unlimited pages, unlimited shots, AI storyboards, priority support'
                  : 'Basic plan with limited features',
                images: ['https://your-domain.com/indieshots-logo.png'], // Add your logo URL
              },
              unit_amount: Math.round(data.amount * 100), // Convert to cents
            },
            quantity: 1,
          },
        ],
        mode: 'subscription', // Change to 'payment' for one-time payments
        success_url: `${this.config.successUrl}?session_id={CHECKOUT_SESSION_ID}`,
        cancel_url: this.config.cancelUrl,
        customer_email: data.customerEmail,
        metadata: {
          userId: data.userId,
          tier: data.tier,
          plan: `indieshots_${data.tier}`,
        },
        subscription_data: {
          metadata: {
            userId: data.userId,
            tier: data.tier,
          },
        },
        billing_address_collection: 'auto',
        automatic_tax: {
          enabled: true,
        },
      };

      const session = await this.stripe.checkout.sessions.create(sessionParams);

      console.log('Stripe checkout session created:', {
        sessionId: session.id,
        userId: data.userId,
        tier: data.tier,
        amount: data.amount
      });

      return session;
    } catch (error) {
      console.error('Failed to create Stripe checkout session:', error);
      throw error;
    }
  }

  /**
   * Retrieve checkout session
   */
  async retrieveSession(sessionId: string): Promise<Stripe.Checkout.Session> {
    try {
      return await this.stripe.checkout.sessions.retrieve(sessionId, {
        expand: ['customer', 'subscription']
      });
    } catch (error) {
      console.error('Failed to retrieve checkout session:', error);
      throw error;
    }
  }

  /**
   * Verify webhook signature
   */
  verifyWebhookSignature(payload: string | Buffer, signature: string): Stripe.Event {
    try {
      return this.stripe.webhooks.constructEvent(
        payload,
        signature,
        this.config.webhookSecret
      );
    } catch (error) {
      console.error('Webhook signature verification failed:', error);
      throw new Error('Invalid webhook signature');
    }
  }

  /**
   * Handle successful payment
   */
  async handleSuccessfulPayment(session: Stripe.Checkout.Session): Promise<{
    userId: string;
    tier: string;
    customerId?: string;
    subscriptionId?: string;
  }> {
    const userId = session.metadata?.userId;
    const tier = session.metadata?.tier;

    if (!userId || !tier) {
      throw new Error('Missing required metadata in session');
    }

    return {
      userId,
      tier,
      customerId: typeof session.customer === 'string' ? session.customer : session.customer?.id,
      subscriptionId: typeof session.subscription === 'string' ? session.subscription : session.subscription?.id,
    };
  }

  /**
   * Create price for subscription
   */
  async createPrice(productId: string, amount: number, currency: string = 'usd', interval: 'month' | 'year' = 'month'): Promise<Stripe.Price> {
    try {
      return await this.stripe.prices.create({
        unit_amount: Math.round(amount * 100),
        currency,
        recurring: {
          interval,
        },
        product: productId,
      });
    } catch (error) {
      console.error('Failed to create Stripe price:', error);
      throw error;
    }
  }

  /**
   * Create product
   */
  async createProduct(name: string, description?: string): Promise<Stripe.Product> {
    try {
      return await this.stripe.products.create({
        name,
        description,
        type: 'service',
      });
    } catch (error) {
      console.error('Failed to create Stripe product:', error);
      throw error;
    }
  }

  /**
   * Cancel subscription
   */
  async cancelSubscription(subscriptionId: string): Promise<Stripe.Subscription> {
    try {
      return await this.stripe.subscriptions.update(subscriptionId, {
        cancel_at_period_end: true,
      });
    } catch (error) {
      console.error('Failed to cancel subscription:', error);
      throw error;
    }
  }

  /**
   * Get customer's subscriptions
   */
  async getCustomerSubscriptions(customerId: string): Promise<Stripe.Subscription[]> {
    try {
      const subscriptions = await this.stripe.subscriptions.list({
        customer: customerId,
        status: 'active',
      });
      return subscriptions.data;
    } catch (error) {
      console.error('Failed to get customer subscriptions:', error);
      throw error;
    }
  }

  /**
   * Get publishable key for frontend
   */
  getPublishableKey(): string {
    return this.config.publishableKey;
  }
}

