import { Router } from 'express';
import { Request, Response } from 'express';

const router = Router();

/**
 * POST /api/auth-bypass/force-pro
 * Force update user to pro tier (for post-payment processing)
 */
router.post('/force-pro', async (req: Request, res: Response) => {
  try {
    const { email } = req.body;

    if (!email) {
      return res.status(400).json({ error: 'Email required' });
    }

    console.log('üîß AUTH BYPASS: Forcing pro tier for:', email);

    const { storage } = await import('../storage');
    const user = await storage.getUserByEmail(email);

    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Force update to pro tier with all pro features
    const proUserData = {
      ...user,
      tier: 'pro',
      totalPages: -1,
      maxShotsPerScene: -1,
      canGenerateStoryboards: true,
      paymentStatus: 'paid',
      upgradeDate: new Date().toISOString()
    };

    await storage.updateUser(user.id, proUserData);

    console.log('‚úÖ AUTH BYPASS: User upgraded to pro tier');

    res.json({
      success: true,
      message: 'User upgraded to pro tier',
      user: proUserData
    });

  } catch (error) {
    console.error('‚ùå AUTH BYPASS ERROR:', error);
    res.status(500).json({ 
      error: 'Failed to upgrade user', 
      details: error instanceof Error ? error.message : 'Unknown error' 
    });
  }
});

/**
 * GET /api/auth-bypass/user/:email
 * Get user data bypassing authentication
 */
router.get('/user/:email', async (req: Request, res: Response) => {
  try {
    const { email } = req.params;

    console.log('üîç AUTH BYPASS: Getting user data for:', email);

    const { storage } = await import('../storage');
    const user = await storage.getUserByEmail(email);

    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Return user data with forced consistency
    const userData = {
      id: user.id,
      email: user.email,
      tier: user.tier,
      totalPages: user.tier === 'pro' ? -1 : 10,
      maxShotsPerScene: user.tier === 'pro' ? -1 : 5,
      canGenerateStoryboards: user.tier === 'pro',
      displayName: user.displayName
    };

    console.log('‚úÖ AUTH BYPASS: Returning user data:', userData);
    res.json(userData);

  } catch (error) {
    console.error('‚ùå AUTH BYPASS ERROR:', error);
    res.status(500).json({ 
      error: 'Failed to get user data', 
      details: error instanceof Error ? error.message : 'Unknown error' 
    });
  }
});

export default router;

server/routes/currency.ts.content:
import { Router, Request, Response } from 'express';
import { currencyConverter } from '../services/currencyConverter';

const router = Router();

/**
 * GET /api/currency/subscription-pricing
 * Get subscription pricing in both INR and USD
 */
router.get('/subscription-pricing', async (req: Request, res: Response) => {
  try {
    console.log('Fetching subscription pricing with currency conversion...');

    const pricing = await currencyConverter.getSubscriptionPricing();

    console.log('Pricing fetched:', {
      inr: pricing.inr.display,
      usd: pricing.usd.display,
      rate: pricing.exchangeRate
    });

    res.json({
      success: true,
      pricing,
      timestamp: new Date().toISOString()
    });

  } catch (error) {
    console.error('Currency conversion error:', error);

    // Return fallback pricing if conversion fails
    res.json({
      success: false,
      pricing: {
        inr: { amount: 999, display: '‚Çπ999', currency: 'INR' },
        usd: { amount: 12, display: '$12', currency: 'USD' },
        exchangeRate: 0.012,
        note: 'Using fallback exchange rate due to API error'
      },
      error: 'Currency conversion temporarily unavailable',
      timestamp: new Date().toISOString()
    });
  }
});

/**
 * GET /api/currency/convert/:amount
 * Convert specific INR amount to USD
 */
router.get('/convert/:amount', async (req: Request, res: Response) => {
  try {
    const inrAmount = parseFloat(req.params.amount);

    if (isNaN(inrAmount) || inrAmount <= 0) {
      return res.status(400).json({
        success: false,
        error: 'Invalid amount. Please provide a positive number.'
      });
    }

    const conversion = await currencyConverter.convertINRtoUSD(inrAmount);

    res.json({
      success: true,
      conversion: {
        ...conversion,
        display: currencyConverter.formatCurrencyDisplay(conversion.inrAmount, conversion.usdAmount)
      },
      timestamp: new Date().toISOString()
    });

  } catch (error) {
    console.error('Currency conversion error:', error);
    res.status(500).json({
      success: false,
      error: 'Currency conversion failed',
      timestamp: new Date().toISOString()
    });
  }
});

export default router;

